/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {

	if(gfn_showConfirm("업무구분을 지정하시겠습니까?") )
	{
		var strArr = [];  // 체크된 항목의 JQPR.NO를 세팅하는 변수
		var jqprStr = ""; // 업무구분 지정 팝업에 JQPR.NO를 전달해주는 변수

		if(ds_mm031.FindRow("CHECK", 1) == -1) {
			gfn_showAlert("처리할 자료를 먼저 선택해 주십시오.");
			return;
		}

		for(var i=0; i<ds_mm031.rowcount(); i++) {
			if(ds_mm031.GetColumn(i, "CHECK") == true) {
				// JQPR.NO 세팅
				strArr[i] = ds_mm031.GetColumn(i, "JQPRNUM");
			}
		}

		var arg = "";

		jqprStr = replace(quote(strArr), "[", "");
		jqprStr = replace(jqprStr, "]", "");

		arg = " fa_jqprnum=" + jqprStr;
		var rtn = gfn_openPopup("CS0102001I",true, arg);

		if(rtn != null) { // 저장 후 조회쿼리
			fn_query();
		}
	}        

}
