/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 필수입력조건 체크내역 해제
	posid_yn = false; // 호기번호 입력여부
	reqno_yn = false; // 문서번호 입력여부
	jqprnum_yn = false; // MMPR.번호 입력여부

	// 디테일 정보 필터링
	if (ds_mm031.rowcount > 0)
	{
		gfn_copyDs(ds_mm031, ds_mm031_2,);
		ds_list.filter("REQNO == " + quote( ds_mm031.GetColumn(0,"REQNO")));

		for(var i = 0; i < ds_mm031.rowcount ; i++)
		{
			if (length(ds_mm031.GetColumn(i, "FNAM")) > 0)
			{
			   ds_mm031.SetColumn(i,"L_FNAM", tit_getLocalFileName(ds_mm031.getColumn(i, "FNAM")));
			}
		}		
	}

	/*if (ds_list.rowcount > 0)
	{
		gfn_copyDs(ds_list, ds_list_4);
	}*/

// 메시지 처리 
//	gfn_showSearchMsg(grd_list);
	gfn_showMsg("CI00002",ds_mm031.RowCount()+"");
}

function fn_afterSave(errCode, errMsg){
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

