function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}

function ufn_ViewMMPR(obj)
{
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	var chksize = 0;
	var isMMPR = false;
	var nRow = 0;
	for (var i = 0; i < ds_mm031.rowcount ; i++){
		if (ds_mm031.GetColumn(i,"CHECK") == "1"){
			nRow = i;
			chksize++;
		}
	}

	if (chksize != 1){
		gfn_showMsg("CW00013");
		return;
	}

	if (length(ds_mm031.GetColumn(nRow,"JQPRNUM")) > 0){
		gfn_openPopup("CS0101001B", true, "fa_jqprnum=" + quote(ds_mm031.GetColumn(nRow,"JQPRNUM")), 610, 523);
	}
	else
	{
		gfn_showMsg("CW00065", "하나의 조회할 MMPR대상을");
		return;
	}
}

// WBS 검색
function btn_Hogi_OnClick(obj)
{
	var pjt= null;
	pjt = gfn_openPopup("CS9601005A", true);

    if (pjt != null && obj == btn_posid2) {
		ed_pjt.value = pjt[0];
    }
    else if (posid != null){
		ed_pjt.value = pjt[0];
    }
}


