********************************************************************************/
function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{


	if ( nCell == 4 && length( ds_mm031.getColumn(nRow, "FNAM")) > 0 ) {
	// 기존 소스 : 파일을 바로 저장
/*	
		saveDialog.FileName = tit_getLocalFileName(ds_mm031.getColumn(nRow, "FNAM"));
		saveDialog.open();
		if ( length(saveDialog.FilePath) == 0 ) {
			return;
		}

