			, saveDialog.FilePath, saveDialog.FileName);
		if ( arr[0] == 'E' ) {
			gfn_alert("Download failed : \n" + arr[1] );
		}		
*/

		// 변경소스 : 파일을 바로 PC에서 실행
		  fn_createWait("", this);

		var FilePath = "c:\\Temp" ;		
		var FileExec = "c:\\Temp\\" + ds_mm031.getColumn(nRow, "FNAM");

		saveDialog.FileName = ds_mm031.getColumn(nRow, "FNAM");

		var rMsg = tit_fileRead(
									httpFile
									, "AP"  	  // 저장 디렉토리
									, ds_mm031.getColumn(nRow, "FNAM")	// 서버에서 전송받을 이름
									, FilePath    // 로컬 경로
									, ds_mm031.getColumn(nRow, "FNAM")    // 로컬 파일명
							   );

		fn_destroyWait();
		if ( rMsg[0] == "E" ) {
			if ( length(trim(rMsg[1])) > 0 ) {
				 //  ExecShell(FileExec);
				gfn_showAlert("CW10025", rMsg[1]);
			} else {
				gfn_showAlert("CW10025", "파일 다운로드에 실패하였습니다.");
			}
		} else {	
				ExecShell(FileExec);		
		}			



	}	
}

/********************************************************************************
