********************************************************************************/
function cal_redat_fm_CanChange(obj,strOldText,strText)
{
	var curDate = gfn_getCurrDate();
	if(strText > curDate) {
		gfn_showAlert("현재일자 이후의 조회기간은 선택하실수 없습니다. ");
		cal_redat_fm.SetFocus();
		return false;
	}
}
