// 필수조건 체크
function searchValueChk() {
	if(length(trim(ds_cond.GetColumn(0,"POSID")))) { // 호기번호가 Null값이 아니면
		posid_yn = true;
	}

	if(length(trim(ds_cond.GetColumn(0,"REQNO")))) { // 문서번호가 Null값이 아니면
		reqno_yn = true;
	}

	if(length(trim(ds_cond.GetColumn(0,"JQPRNUM")))) { // MMPR.no Null값이 아니면
		jqprnum_yn = true;
	}
}

