#include "LIB::commons.js";    // 공통 스크립트
#include "LIB::FormResize_sd.js";    // 공통 스크립트
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다.
//=========================================================================================
var tabindex;
var gRowIndex=-1;
var gRowIndex1=-1;

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
    gfn_initForm(true);             // Form 로드 이벤트 맨 첫줄에 기술

    // 부모창에서 청구분류 데이터 셑을 카피한다.
    ds_reqkind.ClearData();
    ds_reqkind.Copy(parent.ds_reqkind);
    ds_reqkind.filter("CODE = '' OR CODE = '7' OR CODE = '1' OR CODE = 'S'");
    ds_cond.SetColumn(0, "REQKIND", parent.cb_reqkind.value);

    // 사업부서 공용코드 데이터 카피
    ds_bussi.ClearData();
    ds_bussi.Copy(parent.ds_bussi);

    ds_cond.ClearData();
    ds_cond.Copy(parent.ds_cond);
    ds_cond.AddColumn("PARAMTYPE");
    ds_cond.AddColumn("PARAMCTYPE");
    ds_cond.AddColumn("PARAMNCYS");
    ds_cond.AddColumn("NUM");
    ds_cond.AddColumn("TYPE");
    ds_cond.AddColumn("NAM");

    fn_init_form();             // 폼 초기화
}

/********************************************************************************
* 기      능   : form UnloadCompleted
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
    return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {
    tab.TabIndex = 0;
    // 부모창에서 선택된 프로젝트 리스트를 ds_zmmt031에 표시한다.
    ds_mm031_target.ClearData();
    ds_mm031_target.fireevent = false;
    for(var i=0; i < parent.ds_mm031.GetRowCount(); i++) {
        if( parent.ds_mm031.GetColumn(i, "CHECK") == 1 ) {
            var rowIndex = ds_mm031_target.AddRow();
            ds_mm031_target.SetColumn(rowIndex, "CHECK", 1);
            ds_mm031_target.SetColumn(rowIndex, "POSID2", parent.ds_mm031.GetColumn(i, "POSID2"));
            ds_mm031_target.SetColumn(rowIndex, "SPT", parent.ds_mm031.GetColumn(i, "SPT"));
            ds_mm031_target.SetColumn(rowIndex, "REQNO", parent.ds_mm031.GetColumn(i, "REQNO"));

            // 계약관련 정보를 입력해 줘야 함
            ds_mm031_target.SetColumn(rowIndex, "VKGRP", parent.ds_mm031.GetColumn(i, "VKGRP"));
            ds_mm031_target.SetColumn(rowIndex, "VKBUR", parent.ds_mm031.GetColumn(i, "VKBUR"));
            ds_mm031_target.SetColumn(rowIndex, "VBELN", parent.ds_mm031.GetColumn(i, "VBELN"));
            ds_mm031_target.SetColumn(rowIndex, "BSTDK", parent.ds_mm031.GetColumn(i, "BSTDK"));
            ds_mm031_target.SetColumn(rowIndex, "BSTKD", parent.ds_mm031.GetColumn(i, "BSTKD"));
            ds_mm031_target.SetColumn(rowIndex, "VDATU", parent.ds_mm031.GetColumn(i, "VDATU"));
            ds_mm031_target.SetColumn(rowIndex, "KUNNR", parent.ds_mm031.GetColumn(i, "KUNNR"));
            ds_mm031_target.SetColumn(rowIndex, "CONTR_DA", parent.ds_mm031.GetColumn(i, "CONTR_DA"));
            ds_mm031_target.SetColumn(rowIndex, "MATNR", parent.ds_mm031.GetColumn(i, "MATNR"));
            ds_mm031_target.SetColumn(rowIndex, "MANAGER", parent.ds_mm031.GetColumn(i, "ZMAN_NM"));
            ds_mm031_target.SetColumn(rowIndex, "MANAGER2", parent.ds_mm031.GetColumn(i, "ZMAN_NM2"));
        }
    }
    ds_mm031_target.fireevent = true;
    ds_mm031_target.row = -1;

    fn_query();
}

/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {
    if(tab.TabIndex = 0) {
        ds_cond.SetColumn(0, "REQNO", trim(tab.BOM_SPEC.ed_reqno.value));
        ds_cond.SetColumn(0, "REQDATE_FM", trim(tab.BOM_SPEC.cal_reqdate_fm.value));
        ds_cond.SetColumn(0, "REQDATE_TO", trim(tab.BOM_SPEC.cal_reqdate_to.value));
        ds_cond.SetColumn(0, "BUSSI", tab.BOM_SPEC.cb_bussi.value);
        ds_cond.SetColumn(0, "QTNUM", trim(tab.BOM_SPEC.ed_qtnum.value));
        ds_cond.SetColumn(0, "PS_PSPID", trim(tab.BOM_SPEC.ed_pspid.value));
        ds_cond.SetColumn(0, "REQKIND", trim(tab.BOM_SPEC.cb_reqkind.value));

        // 보수 콤보 설정
        ufn_getGbn();

        // 입력값 날짜 체크
		if ( !gfn_checkFromToObj(tab.BOM_SPEC.cal_reqdate_fm, tab.BOM_SPEC.cal_reqdate_to, true, tab.BOM_SPEC.st_reqdate.value, 0, true) ) {
			return;
		}

        //  프로젝트 리스트를 조회한다.
        ds_mm031.ClearData();
        ds_zcst802.ClearData();
        tit_clearActionInfo();
        tit_addSingleActionInfo("cs01:CS0108002E_S3");
        tit_callService(
              ""
            , ""
            , "ds_cond=ds_cond"
            , "ds_mm031=ds_mm031"
            , ""
            , "fn_afterQuery"
            , true
            , true
            , false);
    } else if(tab.TabIndex = 1) {
        // 검색조건 데이터셑 입력
        ds_cond.SetColumn(0, "TYPE", tab.BOM_TEMPLET.cb_type.Value);
        ds_cond.SetColumn(0, "NAM", trim(tab.BOM_TEMPLET.ed_nam.Text));
        fn_queryTemplet();
    }
}

/********************************************************************************
* 기      능   : 복사
********************************************************************************/
function fn_copy() {

    var carCopyMsg = "";
    if( rd_car_copy.Value == "Y" ) {
        carCopyMsg = "(배차항목 복사 포함)";
    } else {
        carCopyMsg = "(배차항목 복사 제외)";
    }

    var cCount = ds_mm031_target.CaseCount("CHECK ='1'");

    if(tab.TabIndex = 0) {
        // 서비스 프로젝트 사양 특성을 복사한다.
        // 선택한 복사할 사양 특성이 없으면 알람 처리한다.
        if( ds_mm031.GetRowCount() == 0 ) {
            return;
        }

        if( ds_zcst802.GetRowCount() == 0 ) {
            gfn_openMsgWin("선택하신 프로젝트에는 등록된 사양 특성이 없습니다.", "", "A", "복사");
            return;
        }

        if (gfn_showConfirm("선택하신 총 "+cCount+"건에 대해 등록된 서비스 사양으로 복사하시겠습니까?"+carCopyMsg)) {
            fn_createWait("복사 처리 중 입니다.",this);
            // 등록된 사양특성을 복사한다.
            // SRCPOSID, POSID, VBELN, REQNO
            // PARAMNCYS, PARAMELMM
            // BSTDK, BSTKD, CONTR_DA, KUNNR, MANAGER, MANAGER2, MATNR, VBELN, VDATU, VKBUR, VKGRP
            ds_zcst802_param.SetColumn(0, "SRCPOSID", ds_mm031.GetColumn(ds_mm031.row, "POSID2"));
            for( var i=0; i < ds_mm031_target.GetRowCount(); i++ ) {
                if(ds_mm031_target.GetColumn(i, "CHECK") == 1 ) {
                    ds_zcst802_param.SetColumn(0, "POSID", ds_mm031_target.GetColumn(i, "POSID2"));
                    ds_zcst802_param.SetColumn(0, "VBELN", ds_mm031_target.GetColumn(i, "VBELN"));
                    ds_zcst802_param.SetColumn(0, "REQNO", ds_mm031_target.GetColumn(i, "REQNO"));
                    // PARAMNCYS
                    if( IndexOf(ds_mm031_target.GetColumn(i, "POSID2"), "NC") > -1  ) {
                        // 시스템 공상인 경우
                        ds_zcst802_param.SetColumn(0, "PARAMNCYS", "Y");
                    } else {
                        ds_zcst802_param.SetColumn(0, "PARAMNCYS", "");
                    }

                    if( rd_car_copy.value == "Y" ) {
                        // 배치복사
                        ds_zcst802_param.SetColumn(0, "PARAMELMM", "Y");
                    } else {
                        ds_zcst802_param.SetColumn(0, "PARAMELMM", null);
                    }

                    // BSTDK, BSTKD, CONTR_DA, KUNNR, ZMAN_NM, ZMAN_NM2, MATNR, VBELN, VDATU, VKBUR, VKGRP
                    ds_zcst802_param.SetColumn(0, "BSTDK", ds_mm031_target.GetColumn(i, "BSTDK"));
                    ds_zcst802_param.SetColumn(0, "BSTKD", ds_mm031_target.GetColumn(i, "BSTKD"));
                    ds_zcst802_param.SetColumn(0, "CONTR_DA", ds_mm031_target.GetColumn(i, "CONTR_DA"));
                    ds_zcst802_param.SetColumn(0, "KUNNR", ds_mm031_target.GetColumn(i, "KUNNR"));
                    ds_zcst802_param.SetColumn(0, "ZMAN_NM", ds_mm031_target.GetColumn(i, "ZMAN_NM"));
                    ds_zcst802_param.SetColumn(0, "ZMAN_NM2", ds_mm031_target.GetColumn(i, "ZMAN_NM2"));
                    ds_zcst802_param.SetColumn(0, "MATNR", ds_mm031_target.GetColumn(i, "MATNR"));
                    ds_zcst802_param.SetColumn(0, "VBELN", ds_mm031_target.GetColumn(i, "VBELN"));
                    ds_zcst802_param.SetColumn(0, "VDATU", ds_mm031_target.GetColumn(i, "VDATU"));
                    ds_zcst802_param.SetColumn(0, "VKBUR", ds_mm031_target.GetColumn(i, "VKBUR"));
                    ds_zcst802_param.SetColumn(0, "VKGRP", ds_mm031_target.GetColumn(i, "VKGRP"));

                    tit_clearActionInfo();
                    tit_addMultiActionInfo("cs01:CS0108002E_I1");
                    tit_callService(
                          ""
                        , ""
                        , "ds_zcst802_param=ds_zcst802_param"
                        , ""
                        , ""
                        , "fn_afterQuerySpecCopy"
                        , true
                        , true
                        , false);
                }
            }
            fn_destroyWait();
            gfn_openMsgWin("사양복사가 완료 되었습니다.", "", "A", "복사");
        }
    } else if(tab.TabIndex = 1) {
        // 템플릿 사양 특성을 복사한다.
        if( ds_zcstplms.GetRowCount() == 0 ) {
            return;
        }

        if( ds_zcstpldt.GetRowCount() == 0 ) {
            gfn_openMsgWin("선택하신 템플릿에는 등록된 사양 특성이 없습니다.", "", "A", "복사");
            return;
        }

        if (gfn_showConfirm("선택하신 총 "+cCount+"건에 대해 등록된 템플릿 사양으로 복사하시겠습니까?"+carCopyMsg)) {
            fn_createWait("복사 처리 중 입니다.",this);
            // 등록된 사양특성을 복사한다.
            // SRCPOSID, POSID, VBELN, REQNO
            // PARAMNCYS, PARAMELMM
            // BSTDK, BSTKD, CONTR_DA, KUNNR, MANAGER, MANAGER2, MATNR, VBELN, VDATU, VKBUR, VKGRP
            ds_zcst802_param.SetColumn(0, "SRCNUM", ds_zcstplms.GetColumn(ds_zcstplms.row, "NUM"));
            for( var i=0; i < ds_mm031_target.GetRowCount(); i++ ) {
                if(ds_mm031_target.GetColumn(i, "CHECK") == 1 ) {
                    ds_zcst802_param.SetColumn(0, "POSID", ds_mm031_target.GetColumn(i, "POSID2"));
                    ds_zcst802_param.SetColumn(0, "VBELN", ds_mm031_target.GetColumn(i, "VBELN"));
                    ds_zcst802_param.SetColumn(0, "REQNO", ds_mm031_target.GetColumn(i, "REQNO"));
                    // PARAMNCYS
                    if( IndexOf(ds_mm031_target.GetColumn(i, "POSID2"), "NC") > -1  ) {
                        // 시스템 공상인 경우
                        ds_zcst802_param.SetColumn(0, "PARAMNCYS", "Y");
                    } else {
                        ds_zcst802_param.SetColumn(0, "PARAMNCYS", "");
                    }

                    if( rd_car_copy.value == "Y" ) {
                        // 배치복사
                        ds_zcst802_param.SetColumn(0, "PARAMELMM", "Y");
                    } else {
                        ds_zcst802_param.SetColumn(0, "PARAMELMM", null);
                    }

                    // BSTDK, BSTKD, CONTR_DA, KUNNR, ZMAN_NM, ZMAN_NM2, MATNR, VBELN, VDATU, VKBUR, VKGRP
                    ds_zcst802_param.SetColumn(0, "BSTDK", ds_mm031_target.GetColumn(i, "BSTDK"));
                    ds_zcst802_param.SetColumn(0, "BSTKD", ds_mm031_target.GetColumn(i, "BSTKD"));
                    ds_zcst802_param.SetColumn(0, "CONTR_DA", ds_mm031_target.GetColumn(i, "CONTR_DA"));
                    ds_zcst802_param.SetColumn(0, "KUNNR", ds_mm031_target.GetColumn(i, "KUNNR"));
                    ds_zcst802_param.SetColumn(0, "ZMAN_NM", ds_mm031_target.GetColumn(i, "ZMAN_NM"));
                    ds_zcst802_param.SetColumn(0, "ZMAN_NM2", ds_mm031_target.GetColumn(i, "ZMAN_NM2"));
                    ds_zcst802_param.SetColumn(0, "MATNR", ds_mm031_target.GetColumn(i, "MATNR"));
                    ds_zcst802_param.SetColumn(0, "VBELN", ds_mm031_target.GetColumn(i, "VBELN"));
                    ds_zcst802_param.SetColumn(0, "VDATU", ds_mm031_target.GetColumn(i, "VDATU"));
                    ds_zcst802_param.SetColumn(0, "VKBUR", ds_mm031_target.GetColumn(i, "VKBUR"));
                    ds_zcst802_param.SetColumn(0, "VKGRP", ds_mm031_target.GetColumn(i, "VKGRP"));

                    tit_clearActionInfo();
                    tit_addMultiActionInfo("cs01:CS0108002E_I2");
                    tit_callService(
                          ""
                        , ""
                        , "ds_zcst802_param=ds_zcst802_param"
                        , ""
                        , ""
                        , "fn_afterQuerySpecCopy"
                        , true
                        , true
                        , false);
                }
            }
            fn_destroyWait();
            gfn_openMsgWin("사양복사가 완료 되었습니다.", "", "A", "복사");
        }
    }
}

