/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
    if ( errCode != 0 ) {
        // 에러 메세지를 출력한다.
        gfn_showAlert(errMsg);
    } else {
        gfn_clearGridSortMark(grd_prjtsrc);
        gfn_clearGridSortMark(tab.BOM_SPEC.grd_mm031);
        gfn_clearGridSortMark(tab.BOM_SPEC.grd_zcst802);
        if( ds_mm031.GetRowCount() > 0 ) {
            // 복사 리스트에 존재하는 프로젝트 번호값 삭제 처리
            ds_mm031.fireevent = false;
            for(var i=0; i < ds_mm031_target.GetRowCount(); i++) {
                // 조회 결과에 복사 대상 프로젝트 번호 삭제
                var searchRow = ds_mm031.SearchRow("POSID2=\""+ds_mm031_target.GetColumn(i, "POSID2")+"\"");
                if( searchRow > -1 ) {
                    ds_mm031.DeleteRow(searchRow);
                }
            }
            ds_mm031.fireevent = true;

            if( ds_mm031.GetRowCount() > 0 ) {
                // 상세 스펙 내역을 조회한다.
                ds_cond.SetColumn(0, "POSID", ds_mm031.GetColumn(0, "POSID2"));
                gRowIndex = 0;
                fn_querySpec();
            }
        }
    }
}

/********************************************************************************
* 기      능   : 서비스 리스트 선택 후 디테일 출력 이벤트
********************************************************************************/
function grd_list_zcst802OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    if( gRowIndex != nRow ) {
        ds_cond.SetColumn(0, "POSID", ds_mm031.GetColumn(nRow, "POSID2"));
        gRowIndex = nRow;
        fn_querySpec();
    }
}

