/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQueryTemplet(errCode, errMsg) {
    if ( errCode != 0 ) {
        // 에러 메세지를 출력한다.
        gfn_showAlert(errMsg);
    } else {
        gfn_clearGridSortMark(tab.BOM_TEMPLET.grd_zcstplms);
        gfn_clearGridSortMark(tab.BOM_TEMPLET.grd_zcstpldt);
        if( ds_zcstplms.GetRowCount() > 0 ) {
            ds_cond.SetColumn(0, "NUM", ds_zcstplms.GetColumn(0, "NUM"));
            gRowIndex1 = 0;
            fn_queryTempletDt();
        }
    }
}

/********************************************************************************
