        if( ds_zcstplms.GetRowCount() > 0 ) {
            ds_cond.SetColumn(0, "NUM", ds_zcstplms.GetColumn(nRow, "NUM"));
            gRowIndex1 = nRow;
            fn_queryTempletDt();
        }
    }
}

