/********************************************************************************
* 기      능   : 원프로젝트 팝업
********************************************************************************/
function btn_pjt_OnClick(obj)
{

    var arg = "pjt="+quote(tab.BOM_SPEC.ed_pspid.value);
    var pjt= null;
    pjt = gfn_openPopup("CS9601002A",true,arg);

    if (pjt != null) {
        tab.BOM_SPEC.ed_pspid.value = pjt[0];
    }
}

/********************************************************************************
* 기      능   : 문서번호 조회
********************************************************************************/
function ufn_getReqNo(obj)
{
    var reqcond = null;
    reqcond = gfn_openPopup("MM0901003A", true);

    if (reqcond !=null) {
        tab.BOM_SPEC.ed_reqno.value = reqcond[0];
    }
}

/********************************************************************************
* 기      능   : 보수 콤보 설정
********************************************************************************/
function ufn_getGbn() {
    var user_grp = gfn_getUserGroup();

    if(user_grp == "10") { // 보수협력사일 경우
        ds_cond.SetColumn(0, "BSU", G_LGORT);
        ds_cond.SetColumn(0, "TEM", '');
        ds_cond.SetColumn(0, "BPM", '');
        ds_cond.SetColumn(0, "ARA", '');
    } else if(user_grp == "20") { // 보수PM일 경우
        ds_cond.SetColumn(0, "BSU", trim(tab.BOM_SPEC.div_code.cb_small.value));
        ds_cond.SetColumn(0, "TEM", '');
        ds_cond.SetColumn(0, "BPM", G_USER_ID);
        ds_cond.SetColumn(0, "ARA", '');
    } else if(user_grp == "30") { // 지방사무소일 경우
        ds_cond.SetColumn(0, "BSU", trim(tab.BOM_SPEC.div_code.cb_small.value));
        ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
        ds_cond.SetColumn(0, "BPM", '');
        ds_cond.SetColumn(0, "ARA", '');
    } else if(user_grp == "40") { // 고객지원부일 경우
        ds_cond.SetColumn(0, "BSU", trim(tab.BOM_SPEC.div_code.cb_small.value));
        if(tab.BOM_SPEC.div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
            ds_cond.SetColumn(0, "DPT", '');
            ds_cond.SetColumn(0, "TEM", '');
            ds_cond.SetColumn(0, "BPM", '');
            ds_cond.SetColumn(0, "ARA", trim(tab.BOM_SPEC.div_code.cb_mid.value));
        } else if(tab.BOM_SPEC.div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
            ds_cond.SetColumn(0, "DPT", '');
            ds_cond.SetColumn(0, "TEM", '');
            ds_cond.SetColumn(0, "BPM", trim(tab.BOM_SPEC.div_code.cb_mid.value));
            ds_cond.SetColumn(0, "ARA", '');
        } else if(tab.BOM_SPEC.div_code.cb_big.value == '4') { // 협력사 콤보1이 부서일 경우
            ds_cond.SetColumn(0, "DPT", trim(tab.BOM_SPEC.div_code.cb_mid.value));
            ds_cond.SetColumn(0, "TEM", trim(tab.BOM_SPEC.div_code.cb_small.value));
            ds_cond.SetColumn(0, "BSU", '');
            ds_cond.SetColumn(0, "BPM", '');
            ds_cond.SetColumn(0, "ARA", '');
        } else { // 협력사 콤보1이 팀일 경우
            ds_cond.SetColumn(0, "DPT", '');
            ds_cond.SetColumn(0, "TEM", trim(tab.BOM_SPEC.div_code.cb_mid.value));
            ds_cond.SetColumn(0, "BPM", '');
            ds_cond.SetColumn(0, "ARA", '');
        }
    }
}
