#include "LIB::commons.js";    // 공통 스크립트
#include "LIB::utility.js";    // 다중 선택
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다.
//=========================================================================================
var maxSpec = 20;
var gRowIndex=-1;

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
    // Form 로드 이벤트 맨 첫줄에 기술
    gfn_initForm(true);

    cb_trans.value = "ALL";
    cb_dflag.value = "ALL";
}

/********************************************************************************
* 기      능   : form UnloadCompleted
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
    return gfn_isFormUnload(obj);
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {

    // 검색조건 데이터셑에 입력한다.
    ds_cond.SetColumn(0, "CTYPE", cb_ctype.value);
    ds_cond.SetColumn(0, "TYPE", cb_type.value);
    ds_cond.SetColumn(0, "DFLAG", cb_dflag.value);
    ds_cond.SetColumn(0, "TRANS", cb_trans.value);
    ds_cond.SetColumn(0, "BLOCKNO", ed_blockno.value);
    ds_cond.SetColumn(0, "BLOCKNOSEQ", ed_blocknoseqn.value);
    ds_cond.SetColumn(0, "MESSAGE", ed_message.value);
    ds_cond.SetColumn(0, "MESSAGE", ed_message.value);
    ds_cond.SetColumn(0, "SPEC", ed_spec.value);

    ds_zcstmatchms.ClearData();
    ds_zcstmatchdt.ClearData();
    ds_spec_column.ClearData();
	tit_clearActionInfo();
	tit_addSingleActionInfo("cs01:CS0108007A_S1");
	tit_addSingleActionInfo("cs01:CS0108007A_S2");
    if( ds_zcst800.GetRowCount() == 0 ) {
	    tit_addSingleActionInfo("cs01:CS0108007B_S1");
    }
	tit_callService(
		  ""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zcstmatchms=ds_zcstmatchms ds_zcstmatchdt=ds_zcstmatchdt ds_zcst800=ds_zcst800"
		, ""
		, "fn_afterQuery"
		, true
		, true
		, true);
}

/********************************************************************************
* 기      능   : 입력
********************************************************************************/
function fn_input() {
    var index = ds_zcstmatchms.row;
    if( index > -1 ) {
		var rowType = ds_zcstmatchms.GetRowType(gRowIndex);
		if( rowType == "insert" )  {
			gfn_openMsgWin("입력 중인 정합성 마스터 데이터를 저장하십시요", "", "A", "");
		} else {
			var rowIndex = ds_zcstmatchms.AddRow();
			ds_zcstmatchms.SetColumn(rowIndex, "BLOCKNO", "");
			ds_zcstmatchms.SetColumn(rowIndex, "DFLAG", "N");
			gRowIndex = rowIndex;
		}
    } else {
		var rowIndex = ds_zcstmatchms.AddRow();
		ds_zcstmatchms.SetColumn(rowIndex, "BLOCKNO", "");
		ds_zcstmatchms.SetColumn(rowIndex, "DFLAG", "N");
		gRowIndex = rowIndex;
    }
}

/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() {
    var index = ds_zcstmatchms.row;
    if( index > -1 ) {
		var rowType = ds_zcstmatchms.GetRowType(gRowIndex);
		if( rowType == "insert" )  {
		    // 현재 필터된 디테일 자료도 삭제한다.
		    ds_spec_column.fireevent = false;
		    for(var i=ds_spec_column.GetRowCount()-1; i > -1; i--) {
		        if( ds_spec_column.GetColumn(i, "BLOCKNO") == "" ) {
		            ds_spec_column.DeleteRow(i);
		        }
		    }
            ds_spec_column.fireevent = true;

			// 현재 선택된 그리드를 삭제한다.
			ds_zcstmatchms.DeleteRow(ds_zcstmatchms.row);

		} else {
			gfn_openMsgWin("DB에 저장된 정합성 체크 데이터는 삭제 할 수 없습니다. \n사용구분을 미사용 변경 후 저장하십시요", "", "A", "");
		}
    }
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
    // 입력값 체크를 수행한다.
    // 정함성 마스터 데이터 셑-입력, 저장, 삭제
    // 입력/수정만 존재한다.
    ds_zcstmatchms_insert.ClearData();
    ds_zcstmatchms_update.ClearData();
    ds_zcstmatchdt_insert.ClearData();
    ds_zcstmatchdt_update.ClearData();
    ds_zcstmatchdt_delete.ClearData();
    if( ds_zcstmatchms.GetUpdate() ) {
        for(var i=0; i < ds_zcstmatchms.GetRowCount(); i++ ) {
            if( ds_zcstmatchms.GetRowType(i) == "insert" || ds_zcstmatchms.GetRowType(i) == "update" ) {
                if( ds_zcstmatchms.GetColumn(i, "CTYPE") == "" || ds_zcstmatchms.GetColumn(i, "CTYPE") == null ) {
                    if( ds_zcstmatchms.GetColumn(i, "BLOCKNO") == "" ) {
                        gfn_openMsgWin("타입을 입력 하세요", "", "A", "저장");
                    } else {
                        gfn_openMsgWin("블럭넘버["+ds_zcstmatchms.GetColumn(i, "BLOCKNO")+"] 타입을 입력 하세요", "", "A", "저장");
                    }
                    return;
                }
                if( ds_zcstmatchms.GetColumn(i, "TYPE") == "" || ds_zcstmatchms.GetColumn(i, "TYPE") == null ) {
                    if( ds_zcstmatchms.GetColumn(i, "BLOCKNO") == "" ) {
                        gfn_openMsgWin("서비스유형을 입력 하세요", "", "A", "저장");
                    } else {
                        gfn_openMsgWin("블럭넘버["+ds_zcstmatchms.GetColumn(i, "BLOCKNO")+"] 서비스유형을 입력 하세요", "", "A", "저장");
                    }
                    return;
                }
                if( ds_zcstmatchms.GetColumn(i, "MESSAGE") == "" || ds_zcstmatchms.GetColumn(i, "MESSAGE") == null ) {
                    if( ds_zcstmatchms.GetColumn(i, "BLOCKNO") == "" ) {
                        gfn_openMsgWin("메세지를 입력 하세요", "", "A", "저장");
                    } else {
                        gfn_openMsgWin("블럭넘버["+ds_zcstmatchms.GetColumn(i, "BLOCKNO")+"] 메세지를 입력 하세요", "", "A", "저장");
                    }
                    return;
                }

                if( ds_zcstmatchms.GetColumn(i, "TRANS") == "N" && ds_zcstmatchms.GetColumn(i, "CTYPE") == "M" ) {
                    gfn_openMsgWin("전송구분을 입력 하세요", "", "A", "저장");
                    return;
                }

                if( ds_zcstmatchms.GetRowType(i) == "insert" ) {
					var rowIndex = ds_zcstmatchms_insert.AddRow();
					ds_zcstmatchms_insert.SetColumn(rowIndex, "MANDT", ds_zcstmatchms.GetColumn(i, "MANDT"));
					ds_zcstmatchms_insert.SetColumn(rowIndex, "CTYPE", ds_zcstmatchms.GetColumn(i, "CTYPE"));
					ds_zcstmatchms_insert.SetColumn(rowIndex, "TYPE", ds_zcstmatchms.GetColumn(i, "TYPE"));
					ds_zcstmatchms_insert.SetColumn(rowIndex, "BLOCKNO", ds_zcstmatchms.GetColumn(i, "BLOCKNO"));
					ds_zcstmatchms_insert.SetColumn(rowIndex, "MESSAGE", ds_zcstmatchms.GetColumn(i, "MESSAGE"));
					if( ds_zcstmatchms.GetColumn(i, "CTYPE") == "D" ) {
					    ds_zcstmatchms_insert.SetColumn(rowIndex, "TRANS", "");
					} else {
					    ds_zcstmatchms_insert.SetColumn(rowIndex, "TRANS", ds_zcstmatchms.GetColumn(i, "TRANS"));
					}
					ds_zcstmatchms_insert.SetColumn(rowIndex, "DFLAG", ds_zcstmatchms.GetColumn(i, "DFLAG"));
                } else if( ds_zcstmatchms.GetRowType(i) == "update" ) {
					var rowIndex = ds_zcstmatchms_update.AddRow();
					ds_zcstmatchms_update.SetColumn(rowIndex, "MANDT", ds_zcstmatchms.GetColumn(i, "MANDT"));
					ds_zcstmatchms_update.SetColumn(rowIndex, "CTYPE", ds_zcstmatchms.GetColumn(i, "CTYPE"));
					ds_zcstmatchms_update.SetColumn(rowIndex, "TYPE", ds_zcstmatchms.GetColumn(i, "TYPE"));
					ds_zcstmatchms_update.SetColumn(rowIndex, "BLOCKNO", ds_zcstmatchms.GetColumn(i, "BLOCKNO"));
					ds_zcstmatchms_update.SetColumn(rowIndex, "MESSAGE", ds_zcstmatchms.GetColumn(i, "MESSAGE"));
					if( ds_zcstmatchms.GetColumn(i, "CTYPE") == "D" ) {
					    ds_zcstmatchms_update.SetColumn(rowIndex, "TRANS", "");
					} else {
					    ds_zcstmatchms_update.SetColumn(rowIndex, "TRANS", ds_zcstmatchms.GetColumn(i, "TRANS"));
					}
					ds_zcstmatchms_update.SetColumn(rowIndex, "DFLAG", ds_zcstmatchms.GetColumn(i, "DFLAG"));
                }
            }
        }
    }

    // 정합성 디테일 입력 자료 데이터 셑을 생성한다.
    // 입력/수정/삭제 처리모두 존재한다.
    if( ds_spec_column.GetUpdate() ) {
        for(var i=0; i < ds_spec_column.GetRowCountNF(); i++) {
            if( ds_spec_column.GetRowTypeNF(i) == "insert" || ds_spec_column.GetRowTypeNF(i) == "update" ) {
                var CTYPE = ds_spec_column.GetColumnNF(i, "CTYPE");
				var BLOCKNO = ds_spec_column.GetColumnNF(i, "BLOCKNO");
				var BLOCKNOSEQ = ds_spec_column.GetColumnNF(i, "BLOCKNOSEQ");
				var DFLAG = ds_spec_column.GetColumnNF(i, "DFLAG");

				var ZORDERLAVEL = "ZORDER";
				var SPECLAVEL = "SPEC";
				var CALCULATLAVEL = "CALCULAT";
				var CALCULATCKLAVEL = "CALCULATCK";
				for(var j=0; j < 20; j++) {
					ZORDERLAVEL = "ZORDER"+ToString(j+1);
					SPECLAVEL = "SPEC"+ToString(j+1);
					CALCULATLAVEL = "CALCULAT"+ToString(j+1);
					CALCULATCKLAVEL = "CALCULATCK"+ToString(j+1);
					if( BLOCKNOSEQ == "" ) {
					    if( length(ds_spec_column.GetColumnNF(i, SPECLAVEL)) == 0 && length(ds_spec_column.GetColumnNF(i, CALCULATLAVEL)) > 0 ) {
                             gfn_openMsgWin("특성코드를 입력 하세요", "", "A", "저장");
					         return; 
					     }
					    if( length(ds_spec_column.GetColumnNF(i, SPECLAVEL)) > 0 && length(ds_spec_column.GetColumnNF(i, CALCULATLAVEL)) == 0  && CTYPE == "M" ) {
                             gfn_openMsgWin("체크값을 입력 하세요", "", "A", "저장");
					         return; 
					     }
					} else {
					    if( length(ds_spec_column.GetColumnNF(i, SPECLAVEL)) == 0 && length(ds_spec_column.GetColumnNF(i, CALCULATLAVEL)) > 0 ) {
                             gfn_openMsgWin("블럭시퀀스["+BLOCKNOSEQ+"] 특성코드를 입력 하세요", "", "A", "저장");
					         return; 
					     }
					    if( length(ds_spec_column.GetColumnNF(i, SPECLAVEL)) > 0 && length(ds_spec_column.GetColumnNF(i, CALCULATLAVEL)) == 0 && CTYPE == "M" ) {
                             gfn_openMsgWin("블럭시퀀스["+BLOCKNOSEQ+"] 체크값을 입력 하세요", "", "A", "저장");
					         return; 
					     }
					}

					// 입력수정 데이터셑 생성
					if( ds_spec_column.GetRowTypeNF(i) == "insert" ) {
                        if( length(ds_spec_column.GetColumnNF(i, SPECLAVEL)) > 0 ) {
							var rowIndex = ds_zcstmatchdt_insert.AddRow();
							ds_zcstmatchdt_insert.SetColumn(rowIndex, "MANDT", ds_spec_column.GetColumnNF(i, "MANDT"));
							ds_zcstmatchdt_insert.SetColumn(rowIndex, "CTYPE", ds_spec_column.GetColumnNF(i, "CTYPE"));
							ds_zcstmatchdt_insert.SetColumn(rowIndex, "TYPE", ds_spec_column.GetColumnNF(i, "TYPE"));
							ds_zcstmatchdt_insert.SetColumn(rowIndex, "BLOCKNO", ds_spec_column.GetColumnNF(i, "BLOCKNO"));
							ds_zcstmatchdt_insert.SetColumn(rowIndex, "BLOCKNOSEQ", ds_spec_column.GetColumnNF(i, "TMPBLOCKNOSEQ"));
							ds_zcstmatchdt_insert.SetColumn(rowIndex, "ZORDER", ToString(j+1));
							ds_zcstmatchdt_insert.SetColumn(rowIndex, "SPEC", ds_spec_column.GetColumnNF(i, SPECLAVEL));
							ds_zcstmatchdt_insert.SetColumn(rowIndex, "CALCULAT", ds_spec_column.GetColumnNF(i, CALCULATLAVEL));
							ds_zcstmatchdt_insert.SetColumn(rowIndex, "CALCULATCK", ds_spec_column.GetColumnNF(i, CALCULATCKLAVEL));
							ds_zcstmatchdt_insert.SetColumn(rowIndex, "DFLAG", DFLAG);
						}
					} else if( ds_spec_column.GetRowTypeNF(i) == "update" ) {
                        if( length(ds_spec_column.GetColumnNF(i, SPECLAVEL)) > 0 ) {
							var rowIndex = ds_zcstmatchdt_update.AddRow();
							ds_zcstmatchdt_update.SetColumn(rowIndex, "MANDT", ds_spec_column.GetColumnNF(i, "MANDT"));
							ds_zcstmatchdt_update.SetColumn(rowIndex, "CTYPE", ds_spec_column.GetColumnNF(i, "CTYPE"));
							ds_zcstmatchdt_update.SetColumn(rowIndex, "TYPE", ds_spec_column.GetColumnNF(i, "TYPE"));
							ds_zcstmatchdt_update.SetColumn(rowIndex, "BLOCKNO", ds_spec_column.GetColumnNF(i, "BLOCKNO"));
							ds_zcstmatchdt_update.SetColumn(rowIndex, "BLOCKNOSEQ", ds_spec_column.GetColumnNF(i, "BLOCKNOSEQ"));
							ds_zcstmatchdt_update.SetColumn(rowIndex, "ZORDER", ToString(j+1));
							ds_zcstmatchdt_update.SetColumn(rowIndex, "SPEC", ds_spec_column.GetColumnNF(i, SPECLAVEL));
							ds_zcstmatchdt_update.SetColumn(rowIndex, "CALCULAT", ds_spec_column.GetColumnNF(i, CALCULATLAVEL));
							ds_zcstmatchdt_update.SetColumn(rowIndex, "CALCULATCK", ds_spec_column.GetColumnNF(i, CALCULATCKLAVEL));
							ds_zcstmatchdt_update.SetColumn(rowIndex, "DFLAG", DFLAG);
						}

						if( length(ds_spec_column.GetColumnNF(i, SPECLAVEL)) == 0 
							&& length(ds_spec_column.GetColumnNF(i, CALCULATLAVEL)) == 0
							&& ds_spec_column.GetOrgColumnNF(i, SPECLAVEL) != null) {
							// 삭제데이터셑 생성
							var rowIndex = ds_zcstmatchdt_delete.AddRow();
							ds_zcstmatchdt_delete.SetColumn(rowIndex, "MANDT", ds_spec_column.GetColumnNF(i, "MANDT"));
							ds_zcstmatchdt_delete.SetColumn(rowIndex, "CTYPE", ds_spec_column.GetColumnNF(i, "CTYPE"));
							ds_zcstmatchdt_delete.SetColumn(rowIndex, "TYPE", ds_spec_column.GetColumnNF(i, "TYPE"));
							ds_zcstmatchdt_delete.SetColumn(rowIndex, "BLOCKNO", ds_spec_column.GetColumnNF(i, "BLOCKNO"));
							ds_zcstmatchdt_delete.SetColumn(rowIndex, "BLOCKNOSEQ", ds_spec_column.GetColumnNF(i, "BLOCKNOSEQ"));
							ds_zcstmatchdt_delete.SetColumn(rowIndex, "ZORDER", ToString(j+1));
                        }
					}
				}
		    }
        }
    }

    if( ds_zcstmatchms_insert.GetRowCount() > 0 
        || ds_zcstmatchms_update.GetRowCount() > 0 
        || ds_zcstmatchdt_insert.GetRowCount() > 0 
        || ds_zcstmatchdt_update.GetRowCount() > 0 
        || ds_zcstmatchdt_delete.GetRowCount() > 0 
        ) {
        if( gfn_showConfirm("저장하시겠습니까?") ) {
            // 수정 처리를 수행한다.
			//trace("ds_zcstmatchms_insert:"+ds_zcstmatchms_insert.SaveXML());
			//trace("ds_zcstmatchms_update:"+ds_zcstmatchms_update.SaveXML());
			//trace("ds_zcstmatchdt_insert:"+ds_zcstmatchdt_insert.SaveXML());
			//trace("ds_zcstmatchdt_update:"+ds_zcstmatchdt_update.SaveXML());
			//trace("ds_zcstmatchdt_delete:"+ds_zcstmatchdt_delete.SaveXML());
			tit_clearActionInfo();
			// 서비스 체크 로직은 별도 Act를 호출하여 처리한다.
			tit_callService(
				  "CS0108007C_ACT"
				, "saveMatchData"
				, "ds_zcstmatchms_insert=ds_zcstmatchms_insert ds_zcstmatchms_update=ds_zcstmatchms_update ds_zcstmatchdt_insert=ds_zcstmatchdt_insert ds_zcstmatchdt_update=ds_zcstmatchdt_update ds_zcstmatchdt_delete=ds_zcstmatchdt_delete"
				, "ds_error=ds_error"
				, ""
				, "fn_afterSave"
				, true
				, true
				, false);
        }
    }
}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
    if (ds_error.rowcount > 0) {
        gfn_showAlert(ds_error.GetColumn(0,"ERRMSG"));
    } else {
        gfn_openMsgWin("정합성 체크 데이터가 저장되었습니다.", "", "A", "저장");
        // 재조회를 수행한다.
        fn_query();
    }
}

/********************************************************************************
* 기      능   : 닫기
********************************************************************************/
function fn_close() {
    if( ds_zcstmatchms.GetUpdate() || ds_spec_column.GetUpdate() ) {
        if( gfn_showConfirm("저장처리 없이 종료하시겠습니까?") ) {
            this.close();
        }
    } else {
        close();
    }
}

/********************************************************************************
* 기      능   : 액셀
********************************************************************************/
function fn_excel() {
    // 그리드에 보이는 값을 엑셀로 출력한다.
    ds_spec_column_excel.ClearData();
    ds_spec_column_excel.Copy(ds_spec_column);
    gfn_saveToExcel(grd_detail_excel, true, "", "", false, true);
}

/********************************************************************************
* 기      능   : print 40
********************************************************************************/
function fn_print() {
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
    if ( errCode != 0 ) {
        gfn_showAlert(errMsg);
    } else {
        gfn_clearGridSortMark(grd_zcstmatchms);
        gfn_showMsg("CI00002",ds_zcstmatchms.RowCount()+"");
        if( ds_zcstmatchdt.GetRowCount() > 0 ) {
            // 특성열 데이터를 
            fn_specChangColumnData();
            gRowIndex = 0;
            // 필터 적용
            var BLOCKNO = ds_zcstmatchms.GetColumn(ds_zcstmatchms.row, "BLOCKNO");
            var CTYPE = ds_zcstmatchms.GetColumn(ds_zcstmatchms.row, "CTYPE");
            ds_spec_column.Filter("BLOCKNO == " + quote(BLOCKNO) + "&& CTYPE == " + quote(CTYPE) );
        }
    }
}

/********************************************************************************
* 기      능   : 특성열 데이터를 행데이터로 변경한다.
********************************************************************************/
function fn_specChangColumnData() {
    var ZORDERCOLUMNNAME = "ZORDER";
    var SPECCOLUMNNAME = "SPEC";
    var CALCULATCOLUMNNAME = "CALCULAT";
    var CALCULATCKCOLUMNNAME = "CALCULATCK";
    var AGOBLOCKNOSEQKEY = ""; // ds_zcstmatchdt.GetColumn(0, "BLOCKNOSEQ");
    var rowIndex = 0;
    var orderIndex = 1;
    var SPECLABEL = "";
    var CALCULATLABEL = "";
    var CALCULATCKLABEL = "";
    var ZORDERLABEL = "";
    ds_spec_column.fireevent = false;

    for(var i=0; i < ds_zcstmatchdt.GetRowCount(); i++) {
        var BLOCKNOSEQKEY = ds_zcstmatchdt.GetColumn(i, "BLOCKNOSEQ")+ds_zcstmatchdt.GetColumn(i, "CTYPE");
        var BLOCKNO = ds_zcstmatchdt.GetColumn(i, "BLOCKNO");
        var BLOCKNOSEQ = ds_zcstmatchdt.GetColumn(i, "BLOCKNOSEQ");
        var ZORDER = ds_zcstmatchdt.GetColumn(i, "ZORDER");
        var SPEC = ds_zcstmatchdt.GetColumn(i, "SPEC");
        var CALCULAT = ds_zcstmatchdt.GetColumn(i, "CALCULAT");
        var CALCULATCK = ds_zcstmatchdt.GetColumn(i, "CALCULATCK");
        var DFLAG = ds_zcstmatchdt.GetColumn(i, "DFLAG");
        var MESSAGE = ds_zcstmatchdt.GetColumn(i, "MESSAGE");
        var TRANS = ds_zcstmatchdt.GetColumn(i, "TRANS");
        var CTYPE = ds_zcstmatchdt.GetColumn(i, "CTYPE");
        var TYPE = ds_zcstmatchdt.GetColumn(i, "TYPE");
        if( BLOCKNOSEQKEY != AGOBLOCKNOSEQKEY ) {
            rowIndex = ds_spec_column.AddRow();
            orderIndex = 1;
            ds_spec_column.SetColumn(rowIndex, "BLOCKNO", BLOCKNO);
            ds_spec_column.SetColumn(rowIndex, "BLOCKNOSEQ", BLOCKNOSEQ);
            ds_spec_column.SetColumn(rowIndex, "DFLAG", DFLAG);
            ds_spec_column.SetColumn(rowIndex, "MESSAGE", MESSAGE);
            ds_spec_column.SetColumn(rowIndex, "TRANS", TRANS);
            ds_spec_column.SetColumn(rowIndex, "CTYPE", CTYPE);
            ds_spec_column.SetColumn(rowIndex, "TYPE", TYPE);
        } else {
            orderIndex = orderIndex + 1;
        }

        ZORDERLABEL = ZORDERCOLUMNNAME + ZORDER;
        SPECLABEL = SPECCOLUMNNAME + ZORDER;
        CALCULATLABEL = CALCULATCOLUMNNAME + ZORDER;
        CALCULATCKLABEL = CALCULATCKCOLUMNNAME + ZORDER;

        ds_spec_column.SetColumn(rowIndex, "MANDT",ds_zcstmatchdt.GetColumn(i, "MANDT"));
        ds_spec_column.SetColumn(rowIndex, ZORDERLABEL,ZORDER);
        ds_spec_column.SetColumn(rowIndex, SPECLABEL, SPEC);
        ds_spec_column.SetColumn(rowIndex, CALCULATLABEL, CALCULAT);
        ds_spec_column.SetColumn(rowIndex, CALCULATCKLABEL, CALCULATCK);
        AGOBLOCKNOSEQKEY = BLOCKNOSEQKEY;
    }
    ds_spec_column.ApplyChange();
    //ds_spec_column.UpdateControl = true;
    ds_spec_column.fireevent = true;
}

/********************************************************************************
* 기      능   : 정합성 마스터 셀클릭시 -> 입력 항목이 있는 경우 제어
********************************************************************************/
function grd_zcstmatchms_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
    var rowType = ds_zcstmatchms.GetRowType(gRowIndex);
    if( rowType == "insert" && gRowIndex != nRow )  {
        gfn_openMsgWin("입력 중인 정합성 마스터 데이터를 저장하십시요", "", "A", "");
        ds_zcstmatchms.row = gRowIndex;  
    } else {
        gRowIndex = nRow;
		var BLOCKNO = ds_zcstmatchms.GetColumn(ds_zcstmatchms.row, "BLOCKNO");
	    var CTYPE = ds_zcstmatchms.GetColumn(ds_zcstmatchms.row, "CTYPE");

		ds_spec_column.Filter("BLOCKNO == " + quote(BLOCKNO) + "&& CTYPE == " + quote(CTYPE) );
    }
}

