{
	var args = "spec="+quote(ed_spec.value);
	    args = args + " grp=''";
	var retnArray=null;
	retnArray = gfn_openPopup("CS0108007B", true, args);

	if( Length(retnArray) == 0 ) {
	} else {
	    ed_spec.value = retnArray[1];
	}
}

