/********************************************************************************
* 기      능   : 셀 클릭 시 특성코드 팝업 호출
********************************************************************************/
function grd_detail_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var args = "spec='' grp=''";
	var retnArray;
	var lavelIndex = nCell/2;
	if( lavelIndex > 0 && nCell > 1 ) {
		retnArray = gfn_openPopup("CS0108007B", true, args);
		var SPECLAVEL = "SPEC"+ToString(lavelIndex);
		var CALCULATCKLAVEL = "CALCULATCK"+ToString(lavelIndex);
		if( Length(retnArray) == 0 ) {
		} else {
			if( retnArray[0] == "EL_CAL" ) {
			    if( retnArray[1] == "" ) {
                    if( ds_spec_column.GetOrgColumn(nRow, SPECLAVEL) == null ) {
						ds_spec_column.SetColumn(nRow, SPECLAVEL, ds_spec_column.GetOrgColumn(nRow, SPECLAVEL));
						ds_spec_column.SetColumn(nRow, CALCULATCKLAVEL, ds_spec_column.GetOrgColumn(nRow, CALCULATCKLAVEL));
                    } else {
						ds_spec_column.SetColumn(nRow, SPECLAVEL, retnArray[1]);
						ds_spec_column.SetColumn(nRow, CALCULATCKLAVEL, "");
                    }
			    } else {
					ds_spec_column.SetColumn(nRow, SPECLAVEL, retnArray[1]);
					ds_spec_column.SetColumn(nRow, CALCULATCKLAVEL, "Y");
			    }
			} else {
			    if( retnArray[1] == "" ) {
                    if( ds_spec_column.GetOrgColumn(nRow, SPECLAVEL) == null ) {
						ds_spec_column.SetColumn(nRow, SPECLAVEL, ds_spec_column.GetOrgColumn(nRow, SPECLAVEL));
						ds_spec_column.SetColumn(nRow, CALCULATCKLAVEL, ds_spec_column.GetOrgColumn(nRow, CALCULATCKLAVEL));
                    } else {
						ds_spec_column.SetColumn(nRow, SPECLAVEL, retnArray[1]);
						ds_spec_column.SetColumn(nRow, CALCULATCKLAVEL, "");
                    }
			    } else {
					ds_spec_column.SetColumn(nRow, SPECLAVEL, retnArray[1]);
					ds_spec_column.SetColumn(nRow, CALCULATCKLAVEL, "");
			    }
			}
		}
	}
}

/********************************************************************************
* 기      능   : 툴팁으로 특성내역을 표시한다.
********************************************************************************/
var gcod="";
var gcnm ="";
function grd_detail_OnMouseOver(obj,nPosX,nPosY,nRow,nCell,nPivotIndex)
{
	var lavelIndex = nCell%2;
	if( lavelIndex == 0 && nCell > 1 ) {
	    if( gcod != obj.GetCellValue(nRow,nCell) ) {
			var searchRow = ds_zcst800.SearchRow("COD="+quote(obj.GetCellValue(nRow,nCell)));
			gcnm = ds_zcst800.GetColumn(searchRow, "CNM");
			grd_detail.ToolTipText = gcnm;
			gcod = obj.GetCellValue(nRow,nCell);
	    } else {
			grd_detail.ToolTipText = gcnm;
	    }
    } else {
	    grd_detail.ToolTipText = "";
    }
}

/********************************************************************************
* 기      능   : 정합성 체크 데이터 그리드 추가
********************************************************************************/
function btn_spec_add_OnClick(obj)
{
    var index = ds_zcstmatchms.row;
    var rowType = ds_zcstmatchms.GetRowType(gRowIndex);
    if( index > -1 ) {
		var rowIndex = ds_spec_column.AddRow();
		var CTYPE = ds_zcstmatchms.GetColumn(index, "CTYPE");
		var TYPE = ds_zcstmatchms.GetColumn(index, "TYPE");
		var DFLAG = ds_zcstmatchms.GetColumn(index, "DFLAG");
		var BLOCKNO = ds_zcstmatchms.GetColumn(index, "BLOCKNO");
		var MESSAGE = ds_zcstmatchms.GetColumn(index, "MESSAGE");
		var TRANS = ds_zcstmatchms.GetColumn(index, "TRANS");
		var TMPSEQN = ToString(rowIndex+1);

		// 타입별로 시퀀스를 다르게 처리한다.
		if( Length(TMPSEQN) == 1 ) {
			 TMPSEQN = "0"+TMPSEQN;
		}

		// duty call 인경우 시퀀스 넘버 3자리로 처리
		if( Length(TMPSEQN) == 1 ) {
			 TMPSEQN = "00"+TMPSEQN;
		}

		if( Length(TMPSEQN) == 2 ) {
			 TMPSEQN = "0"+TMPSEQN;
		}

		var TMPBLOCKNOSEQ = BLOCKNO+TMPSEQN;

		ds_spec_column.SetColumn(rowIndex, "CTYPE", CTYPE);
		ds_spec_column.SetColumn(rowIndex, "TYPE", TYPE);
		ds_spec_column.SetColumn(rowIndex, "BLOCKNO", BLOCKNO);
		ds_spec_column.SetColumn(rowIndex, "BLOCKNOSEQ", "");
		ds_spec_column.SetColumn(rowIndex, "TMPBLOCKNOSEQ", TMPBLOCKNOSEQ);
		ds_spec_column.SetColumn(rowIndex, "DFLAG", DFLAG);

		ds_spec_column.SetColumn(rowIndex, "MESSAGE", MESSAGE);
		ds_spec_column.SetColumn(rowIndex, "TRANS", TRANS);
    }
}

/********************************************************************************
* 기      능   : 정합성 체크 데이터 그리드 삭제
********************************************************************************/
function btn_spec_del_OnClick(obj)
{
    var index = ds_spec_column.row;
    if( index > -1 ) {
        var rowType = ds_spec_column.GetRowType(index);
		if( rowType == "insert" )  {
			ds_spec_column.DeleteRow(index);
		} else {
			gfn_openMsgWin("DB 입력된 정합청 체크 데이터는 삭제 할 수 없습니다. \n사용구분을 미사용 변경 후 저장하십시요", "", "A", "");
		}
    }
}

/********************************************************************************
* 기      능   : 서비스 유형, 서비스 타입 변경 시 그리드값 변경
********************************************************************************/
function ds_zcstmatchms_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "CTYPE" ) {
	    ds_spec_column.fireevent = false;
        for(var i=0; i < ds_spec_column.GetRowCount(); i++) {
            ds_spec_column.SetColumn(i, "CTYPE", varNewValue);
        }	    
	    ds_spec_column.fireevent = true;
	}

	if( strColumnID == "TYPE" ) {
	    ds_spec_column.fireevent = false;
        for(var i=0; i < ds_spec_column.GetRowCount(); i++) {
            ds_spec_column.SetColumn(i, "TYPE", varNewValue);
        }	    
	    ds_spec_column.fireevent = true;
	}

	if( strColumnID == "DFLAG" ) {
	    ds_spec_column.fireevent = false;
        for(var i=0; i < ds_spec_column.GetRowCount(); i++) {
            ds_spec_column.SetColumn(i, "DFLAG", varNewValue);
        }
	    ds_spec_column.fireevent = true;
	}
}

