/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	ds_cond.SetColumn(0, "DPT", '');
	ds_cond.SetColumn(0, "TEM", '');
	ds_cond.SetColumn(0, "BSU", '');
	ds_cond.SetColumn(0, "BPM", '');
	ds_cond.SetColumn(0, "ARA", '');

	// Tab별 전체 조회 시 Month 인자값 없음으로 인한 예외처리 .2020.12.04 HJH
	if(cb_month.Index == 0) {
		if(Tab00.TabIndex == 0 || Tab00.TabIndex == 2 || Tab00.TabIndex == 3 || Tab00.TabIndex == 4) {
			gfn_showAlert("해당탭의 경우 전체 데이터로 조회 할 수 없습니다. 기준년월을 선택해 주세요."); 
			cb_month.SetFocus();
			return;
		}
	}

	var user_grp = gfn_getUserGroup();

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else if(div_code.cb_big.value == '4') { // 협력사 콤보1이 부서일 경우
			ds_cond.SetColumn(0, "DPT", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_small.value));
			ds_cond.SetColumn(0, "BSU", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
	}

	tit_clearActionInfo();

	if (Tab00.TabIndex == 0) {		
		//alert("TAB 1");
		ds_list_t0.ClearData();

		tit_addSearchActionInfo("cs01:CS0109002A_S1");

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list_t0=ds_list_t0"
			, ""
			, "fn_afterQuery0"
			, true);
	} else if(Tab00.TabIndex == 1) {		
//		alert("TAB 2");
		ds_list_t1.ClearData();

		tit_addSearchActionInfo("cs01:CS0109002A_S2");

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list_t1=ds_list_t1"
			, ""
			, "fn_afterQuery1"
			, true);
	} else if(Tab00.TabIndex == 2) {		
//		alert("TAB 3");

		ds_list_t2.ClearData();

		tit_addSearchActionInfo("cs01:CS0109002A_S6");

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list_t2=ds_list_t2"
			, ""
			, "fn_afterQuery2"
			, true);


	} else if(Tab00.TabIndex == 3) {
//		alert("TAB 4");

		ds_list_t3.ClearData();

		tit_addSearchActionInfo("cs01:CS0109002A_S3");

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list_t3=ds_list_t3"
			, ""
			, "fn_afterQuery3"
			, true);


	} else {
//		alert("TAB 5");

		ds_list_t4_h.ClearData();
		ds_list_t4.ClearData();

		tit_addSearchActionInfo("cs01:CS0109002A_S5");

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list_t4_h=ds_list_t4_h"
			, ""
			, "fn_afterQuery4"
			, true);
	}

	//alert(Tab00.TabIndex);
}

