	else if (Tab00.TabIndex == 1) 
		gfn_saveToExcel(Tab00.tab2.grd_list1, true, "", "", false, true);
	else if (Tab00.TabIndex == 2) 
		gfn_saveToExcel(Tab00.tab3.grd_list, true, "", "", false, true);
	else if (Tab00.TabIndex == 3) 
		gfn_saveToExcel(Tab00.tab4.grd_list, true, "", "", false, true);
	else
		gfn_saveToExcel(Tab00.tab5.grd_list, true, "", "", false, true);
}
