/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery0(errCode, errMsg) {
	// 메시지 처리 
	gfn_showSearchMsg(Tab00.tab1.grd_list0);
}

function fn_afterQuery1(errCode, errMsg) {

	var v_ratio;

	//전년 전년비  구하기
	for ( i =0  ; i < 5 ; i++ ) {

		v_ratio = (ds_list_t1.GetColumn(i+5,"MAVG") - ds_list_t1.GetColumn(i,"MAVG")) *100 / ds_list_t1.GetColumn(i,"MAVG") ;
		ds_list_t1.SetColumn(i+5,"MAVG_R", v_ratio);
		v_ratio = (ds_list_t1.GetColumn(i+5,"M01") - ds_list_t1.GetColumn(i,"M01")) *100 / ds_list_t1.GetColumn(i,"M01") ;
		ds_list_t1.SetColumn(i+5,"M01_R", v_ratio);
		v_ratio = (ds_list_t1.GetColumn(i+5,"M02") - ds_list_t1.GetColumn(i,"M02")) *100 / ds_list_t1.GetColumn(i,"M02") ;
		ds_list_t1.SetColumn(i+5,"M02_R", v_ratio);
		v_ratio = (ds_list_t1.GetColumn(i+5,"M03") - ds_list_t1.GetColumn(i,"M03")) *100 / ds_list_t1.GetColumn(i,"M03") ;
		ds_list_t1.SetColumn(i+5,"M03_R", v_ratio);
		v_ratio = (ds_list_t1.GetColumn(i+5,"M04") - ds_list_t1.GetColumn(i,"M04")) *100 / ds_list_t1.GetColumn(i,"M04") ;
		ds_list_t1.SetColumn(i+5,"M04_R", v_ratio);
		v_ratio = (ds_list_t1.GetColumn(i+5,"M05") - ds_list_t1.GetColumn(i,"M05")) *100 / ds_list_t1.GetColumn(i,"M05") ;
		ds_list_t1.SetColumn(i+5,"M05_R", v_ratio);
		v_ratio = (ds_list_t1.GetColumn(i+5,"M06") - ds_list_t1.GetColumn(i,"M06")) *100 / ds_list_t1.GetColumn(i,"M06") ;
		ds_list_t1.SetColumn(i+5,"M06_R", v_ratio);
		v_ratio = (ds_list_t1.GetColumn(i+5,"M07") - ds_list_t1.GetColumn(i,"M07")) *100 / ds_list_t1.GetColumn(i,"M07") ;
		ds_list_t1.SetColumn(i+5,"M07_R", v_ratio);
		v_ratio = (ds_list_t1.GetColumn(i+5,"M08") - ds_list_t1.GetColumn(i,"M08")) *100 / ds_list_t1.GetColumn(i,"M08") ;
		ds_list_t1.SetColumn(i+5,"M08_R", v_ratio);
		v_ratio = (ds_list_t1.GetColumn(i+5,"M09") - ds_list_t1.GetColumn(i,"M09")) *100 / ds_list_t1.GetColumn(i,"M09") ;
		ds_list_t1.SetColumn(i+5,"M09_R", v_ratio);
		v_ratio = (ds_list_t1.GetColumn(i+5,"M10") - ds_list_t1.GetColumn(i,"M10")) *100 / ds_list_t1.GetColumn(i,"M10") ;
		ds_list_t1.SetColumn(i+5,"M10_R", v_ratio);
		v_ratio = (ds_list_t1.GetColumn(i+5,"M11") - ds_list_t1.GetColumn(i,"M11")) *100 / ds_list_t1.GetColumn(i,"M11") ;
		ds_list_t1.SetColumn(i+5,"M11_R", v_ratio);
		v_ratio = (ds_list_t1.GetColumn(i+5,"M12") - ds_list_t1.GetColumn(i,"M12")) *100 / ds_list_t1.GetColumn(i,"M12") ;
		ds_list_t1.SetColumn(i+5,"M12_R", v_ratio);
		v_ratio = (ds_list_t1.GetColumn(i+5,"SUM") - ds_list_t1.GetColumn(i,"SUM")) *100 / ds_list_t1.GetColumn(i,"SUM") ;
		ds_list_t1.SetColumn(i+5,"SUM_R", v_ratio);

	}

	// 메시지 처리 
	gfn_showSearchMsg(Tab00.tab2.grd_list1);
}

function fn_afterQuery2(errCode, errMsg) {
	// 메시지 처리 
	gfn_showSearchMsg(Tab00.tab1.grd_list0);
}


function fn_afterQuery3(errCode, errMsg) {


	var tmp1, tmp2, tmp3;

//Trace(ds_list_t3.SaveXML());	

	for (i = 0 ; i  < 10 ; i=i+3) {
		tmp1 = ds_list_t3.GetColumn(i,"TOT");
		tmp2 = ds_list_t3.GetColumn(i+1,"TOT");
		tmp3 = tmp1 + tmp2;
		ds_list_t3.SetColumn(i,"TOTR",ROUND(tmp1*100/tmp3));
		ds_list_t3.SetColumn(i+1,"TOTR",ROUND(tmp2*100/tmp3));
		ds_list_t3.SetColumn(i+2,"TOTR",'100');
	}

	gfn_showSearchMsg(Tab00.tab3.grd_list);
}



//그리드 헤더 정보 설정
function fn_afterQuery4(errCode, errMsg) {
	Trace(ds_list_t4_h.SaveXML()); //SetCellProp('Head', 0, "text", "asdf");

	for ( i = 0; i < ds_list_t4_h.rowcount ; i++) {
		if (i < 12)
			Tab00.tab5.grd_list.SetCellProp('Head', i+4 , "text", ds_list_t4_h.GetColumn(i,"MATKX"));
		else
			Tab00.tab5.grd_list.SetCellProp('Head', i+6 , "text", ds_list_t4_h.GetColumn(i,"MATKX"));
	}

	//LIST QUERY

	tit_addSearchActionInfo("cs01:CS0109002A_S4");

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list_t4=ds_list_t4"
		, ""
		, "fn_afterQuery5"
		, true);

}

function fn_afterQuery5(errCode, errMsg) {
	gfn_showSearchMsg(Tab00.tab5.grd_list);
}

