function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm();

	//의견입력 비활성화
	tx_apdesc.Enable = false;
	// 결재처리결과 리턴값 초기화
	completeArr = ["",""];

    // 구분 코드값 가져오기
    var codeArr = ["WBAP_DOCKIND","WBAP_STAT","WBAP_SEQSTAT"];
    var dsObjArr = [ds_dockind,ds_stat,ds_seqstat];
    var addTypeArr = [G_CODE_NONE,G_CODE_NONE,G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

    // 문서번호/ 문서 구분 설정
    if(fa_docno != null && fa_dockind != null)
    {
		ds_cond.SetColumn(0,"DOCNO", fa_docno);
		ds_cond.SetColumn(0,"DOCKIND", fa_dockind);    
    } else {
        gfn_showAlert("CW00153");
		return;
    }
/*
    if (length(G_ORG_USER_INFO) > 10 || length(G_DPT1) < 1 )
    {
		gfn_showAlert("CW00053","[사용자 변경 또는 사번 이외의 ID] 사용자 는");
		fn_close();
		return;
    }
*/

	ds_org.ClearData();
	ds_tree.ClearData();

	tit_clearActionInfo();	
	/*************************************미사용 시작*************/	
	//최초버전
	//tit_addSearchActionInfo("cs04:CS0401001A_S1");
	//tit_addSearchActionInfo("cs04:CS0401001A_S2");
	// 해외 한국 담당 조직구조 강제 생성  201701 이하림 
	// 2018년 이전 버전
	//	tit_addSearchActionInfo("cs04:CS0401001A_S1B");
	//	tit_addSearchActionInfo("cs04:CS0401001A_S2B");	
	/*************************************미사용 종료*************/

	tit_addSearchActionInfo("cs04:CS0401001A_TREE");	 // 조직도 ds_tree
	tit_addSearchActionInfo("cs04:CS0401001A_EMPLOYEE"); // 사원 목록 ds_org
	tit_addSearchActionInfo("cs04:CS0401001A_S5");       // 결재자 정보 ds_draft

	tit_callService(
        ""
        , ""
        , ""
        , "ds_org= ds_org ds_tree=ds_tree ds_draft= ds_draft"
        , ""
        , "fn_afterOrg"
        , true);

}
