function bt_report_OnClick()
{
	// 2019.07.17 문서번호없이 결재테이블에 저장되는 버그로 인하여 수정. j.h
	if(ed_docno.Text = '') {
		gfn_showAlert("CW10025", "문서번호가 존재하지 않아 상신이 불가능합니다.");
		return;
	}
	if (ds_cond.GetColumn(0,"APPGUBN") == "S") // 기안부서
	{
		//(수리부품견적)수주금액 1천만원 초과 건 계약서첨부 필수	2016.06 yar
		 if((fa_reqkind == 7 || fa_reqkind == "S") && length( ed_fileName.value ) == 0 && fa_doc == "R02" )
		{
			gfn_showAlert("CW10025", "계약서를 첨부해주시길 바랍니다.<br>(수리공사,시스템공사 계약서 첨부 필수)");
			return;
		}
		 if( fa_workstage == "3" && length( ed_fileName.value) == 0 )
		{
			gfn_showAlert("CW10025", "계약서를 첨부해주시길 바랍니다.<br>(재청구 시 계약서 첨부 필수)");
			return;
		}
		var searchRow = ds_list.SearchRowNF("APNUM='H2005271'");
		if(fa_dockind == "13" && fa_modgbn > "M4" && fa_reqkind == "S" && ds_cond.GetColumn(0, "APPGUBN") == "S" && searchRow < 0 && fa_modgbn <> "M7")
		{
			gfn_showAlert("CW10025", "Package5 또는 신규,전면교체는 한국승강기사업본부 담당자를 결재선에 추가 바랍니다");
			return;
		}				
	}
	if(ds_head.RowCount() < 1)
	{
		return;
	}

	if(ds_list.RowCount() < 1)
	{
		return;
	}

	if(!gfn_showConfirm("CI00014", "결재선" )) return;

	tit_clearActionInfo();
	if (fa_redraft == "Y" && fa_dockind == "1")		// MMPR 재기안 기능 추가
	{
		tit_addMultiActionInfo("cs04:CS0401002A_D1");
		tit_addMultiActionInfo("cs04:CS0401002A_D2");
	}
	ds_list.SetColumn(ds_list.rowcount -1,"APDESC",tx_apdesc.Text);   

	gfn_copyDs(ds_list, ds_list_0);

	if (ds_cond.GetColumn(0,"APPGUBN") == "S")
	{
		// 2020.09.25 Han J.H
		// 기안문서 저장 시 중복체크
		// 2021.01.08 MMPR 재기안의 경우 결재헤더 삭제로직이 존재하여 동일문서 체크되지 않도록 처리 Han J.H
		if(!(fa_redraft == "Y" && fa_dockind == "1")) {
			fn_isDocYnChk();

			if(chk_isDocYn) {        // 동일 문서번호 존재 시 상신처리 하지 않음
				chk_isDocYn = false; // 동일 문서번호 체크 Flag 초기화
				return;
			}				
		}

		ds_head.SetColumn(0,"APSTAT","1");    
		tit_addSingleActionInfo("cs04:CS0401001A_I1");
    } else
    {
		ds_head.SetColumn(0,"APSTAT","4");    
		tit_addSingleActionInfo("cs04:CS0401001A_U1");
    }

// 반송시에만 MMS 전송
	// ds_mms.ClearData();
	// var cellp = "";

	// for (var i = 0 ; i < ds_list_0.rowcount ; i++ )
	// {
	    // if(length(ds_list_0.GetColumn(i,"APNUM")) < 8)
	    // {
			// gfn_showAlert("CW00022","결재선");
			// return;
	    // }

		// if (parseInt(ds_list_0.GetColumn(i,"SEQNO")) > 1 && length(ds_list_0.GetColumn(i,"TELH")) > 0)
		// {
			// ds_mms.AddRow();
			// ds_mms.setColumn(i,"TRAN_ID",G_USER_ID);
			// ds_mms.setColumn(i,"TRAN_PHONE", Replace(ds_list_0.GetColumn(i,"TELH"),"-",""));
			// ds_mms.setColumn(i,"TRAN_CALLBACK",Replace(ds_draft.GetColumn(0,"TELH"),"-",""));

			// if(length(tx_apdesc.text)> 0)
			// {
				// ds_mms.setColumn(i,"TRAN_MSG", "["+cb_dockind.Text + "] 결재가 상신 되었습니다. [의견]"+substr(tx_apdesc.Text,0,50));
			// } else {
				// ds_mms.setColumn(i,"TRAN_MSG", "["+cb_dockind.Text + "] 결재가 상신 되었습니다.");
			// }

			// ds_mms.setColumn(i,"TRAN_ETC1","WEB_APPROVAL");
			// ds_mms.setColumn(i,"TRAN_ETC2", cb_dockind.Text);	
			// ds_mms.setColumn(i,"SUBJECT", "[현장지원 결재관리]");	
		// } 
	// }
	if(length( ds_head_temp.getColumn(0, "CS649_FNA") ) > 0) {
			ds_head.setColumn(0, "FNAM", ds_head_temp.getColumn(0, "CS649_FNA"));
			ds_head.setColumn(0, "FSIZE", ds_head_temp.getColumn(0, "CS649_FSZ"));
	} else {

		if(length( ed_fileName.value ) > 0) {
			if(length(ds_head.getColumn(0, "FILE_PATH")) > 1 ){
				// 첨부 파일 처리 
				var arr = tit_fileWrite(
					httpFile
					, "AP"
					, ds_head.getColumn(0, "FILE_PATH")
					, ds_head.getColumn(0, "FNAM")
					, ""
					, ""
					, "" );  
				// 처리 결과 확인하기 
				if( arr[0] == "S" ) {
					// 성공 
					ds_head.setColumn(0, "FNAM", arr[2]);	// 파일 명 
					ds_head.setColumn(0, "FSIZE", fileSize);
				} else {
					gfn_showAlert( arr[1] );
					return;
				}
			}
		} else {
			ds_head.setColumn(0, "FNAM", "");
			ds_head.setColumn(0, "FSIZE", 0);
		}

	} 

 	//결재자 저장

