 	//tit_addMultiActionInfo("cs04:CS0401001A_I2B"); // 해외 한국 담당 
 	tit_addSingleActionInfo("cs04:CS0401002A_U3");

	tit_callService(
		""
		, ""
		, "ds_head_0=ds_cond ds_head_0=ds_cond ds_head=ds_head ds_list=ds_list ds_list_0=ds_list_0"
		, ""
		, ""
		, "fn_afterSave"
		//, true
		//, false
		, true); // 2020.09.25 SYNC 변경

}

/********************************************************************************
* 기      능   : 문서 반송
********************************************************************************/
function bt_sendback_OnClick()
{
	if(ds_head.RowCount() < 1)
	{
		return;
	}

	if(ds_list.RowCount() < 1)
	{
		return;
	}
	if(ds_list.RowCount() > 1)
	{
	    gfn_showAlert("CW00171","결재선");
		return;
	}

	if(!gfn_showConfirm("CI00014", "반송정보" )) return;

	ds_list.SetColumn(ds_list.rowcount -1,"APDESC",tx_apdesc.Text);   
	ds_list.SetColumn(ds_list.rowcount -1,"SEQSTAT","2");   

	gfn_copyDs(ds_list, ds_list_0);

	tit_clearActionInfo();

	if (ds_cond.GetColumn(0,"APPGUBN") == "S")
	{
		return;
    } else
    {
		ds_head.SetColumn(0,"APSTAT","5");    
		tit_addSingleActionInfo("cs04:CS0401001A_U1");
    }

	if (length(ds_head.GetColumn(i,"TELH")) > 0)
   	{
	    ds_mms.ClearData();
		ds_mms.AddRow();
		ds_mms.setColumn(0,"TRAN_ID",G_USER_ID);
		ds_mms.setColumn(0,"TRAN_PHONE", Replace(ds_head.GetColumn(0,"TELH"),"-",""));
		ds_mms.setColumn(0,"TRAN_CALLBACK",Replace(ds_draft.GetColumn(0,"TELH"),"-",""));

		if(length(tx_apdesc.text)> 0)
		{
			ds_mms.setColumn(0,"TRAN_MSG", "[문서번호:"+ed_docno.Text+" 구분:"+cb_dockind.Text + "] 결재가 반송 되었습니다. [의견]"+substr(tx_apdesc.Text,0,50));
		} else {
			ds_mms.setColumn(0,"TRAN_MSG", "[문서번호:"+ed_docno.Text+" 구분:"+cb_dockind.Text + "] 결재가 반송 되었습니다.");
		}

		ds_mms.setColumn(0,"TRAN_ETC1","WEB_APPROVAL");
		ds_mms.setColumn(0,"TRAN_ETC2", cb_dockind.Text);	
		ds_mms.setColumn(0,"SUBJECT", "[현장지원 결재관리]");	
	}

	// 반송버튼 클릭이벤트여부
	sendbackClick = 'Y';

 	//결재자 저장
 	tit_addMultiActionInfo("cs04:CS0401001A_I2");
 	// CS0401001A_I2B 미사용 20180112 이하림 
 	//tit_addMultiActionInfo("cs04:CS0401001A_I2B"); // 해외 한국 담당 
 	tit_addMultiActionInfo("cs04:CS0401002A_U3");

	tit_callService(
		""
		, ""
		, "ds_head=ds_head ds_list=ds_list ds_list_0=ds_list_0"
		, ""
		, ""
		, "fn_afterSave"
		, true
		, ""
		, true);
}
