// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	if(ds_head.rowcount > 0)
	{
		// 수신부서 이면서 기안부서 결재완료시 상신 버튼 활성화
		if(ds_head.GetColumn(0,"APSTAT") == "3")
		{
		    bt_report.Visible = true;
		    //bt_sendback.Visible = true;
		    //bt_sendback.Enable = true;

            // 수신부서 결재선 상신의 경우 수신 부서, 부서명 설정
		    ds_head.SetColumn(0,"APSTAT", "4");
			ds_head.SetColumn(0,"REDPT1", ds_draft.GetColumn(0,"DPT1"));
			ds_head.SetColumn(0,"REDPT1_N", ds_draft.GetColumn(0,"DPT1_N"));
		    ds_head.SetColumn(0,"REIRUM", G_USER_NAME);
		}
		else if(ds_head.GetColumn(0,"APSTAT") == "2" || ds_head.GetColumn(0,"APSTAT") == "5") // 기안/결재 반송시에는 삭제 가능
		{
		    bt_delete.Enable = true;
		}

        disableFile();

        // 수신부서 결재선 상신의 경우 수신 부서, 부서명 설정

	    // if (ds_head.GetColumn(0,"APSTAT") = "3" && ds_head.GetColumn(0,"REDPT1") == G_DPT1)
	    // {
		    // ds_head.SetColumn(0,"APSTAT", "4");
			// ds_head.SetColumn(0,"REDPT1", ds_draft.GetColumn(0,"DPT1"));
			// ds_head.SetColumn(0,"REDPT1_N", ds_draft.GetColumn(0,"DPT1_N"));
		    // ds_head.SetColumn(0,"REIRUM", G_USER_NAME);

	    // } 
	    // else  // 기안부서 결재선 조회
	    // {
			// ds_cond.SetColumn(0,"APPGUBN","S");
	    // }

	} 
	else
	{
		fn_query3();

		// 결재선 정보가 없는 경우 상신버튼 활성화
		bt_report.Visible = true;

		// 결재선 정보가 없는 경우 최초 기안자의 정보를 입력한다.	
		ds_head.ClearData();
		ds_head.AddRow();

		ds_head.SetColumn(0,"DOCNO", ed_docno.text);
		ds_head.SetColumn(0,"DOCKIND", cb_dockind.Value);
		ds_head.SetColumn(0,"CRDPT1", ds_draft.GetColumn(0,"DPT1"));
		ds_head.SetColumn(0,"CRDPT1_N", ds_draft.GetColumn(0,"DPT1_N"));
		ds_head.SetColumn(0,"CRIRUM", G_USER_NAME);
/*  2014.04.01 이주형 수정(ds_dockind 선택된값 가져오기) */
//		ds_head.SetColumn(0,"REDPT1", ds_dockind.GetColumn(ds_dockind.currow,"DTEXT1"));
		ds_head.SetColumn(0,"REDPT1", ds_dockind.GetColumn(ds_dockind.FindRow("CODE",fa_dockind),"DTEXT1"));
		ds_head.SetColumn(0,"REDPT1_N", ds_tree.GetColumn(ds_tree.FindRow("DPT1",ds_head.GetColumn(0,"REDPT1")),"DPT1_N"));
	    //===============================================전결규정 저장으로 수정===============================================
	    if (fa_dockind == '3' || fa_dockind = '4') // 유상견적, 유상계약 에만 적용
	    {
			ds_head.SetColumn(0,"DISCRULE", fa_discrule);
		}
		else
		{
			ds_head.SetColumn(0,"DISCRULE", '');
		}
	    //====================================================================================================================
		enableFile();
	}
	//  수신 부서결재선 조회
	ds_cond.SetColumn(0,"APPGUBN",fa_appgubn);

	// 결재정보가 있는 경우 결재선 조회 
	fn_query2();
}

function fn_afterQuery2(errCode, errMsg) {
	if(ds_list.rowcount ==  0)
	{
        // 결재선 정보가 없는 경우 최초 기안자를 기본 결재자로 입력
		ds_list.ClearData();
		ds_list.AddRow();

		ds_list.SetColumn(0,"DOCNO", ed_docno.text);
		ds_list.SetColumn(0,"DOCKIND", cb_dockind.Value);

		// 기안결재 완료 상태에서는 수신 상태
		// if(ds_head.GetColumn(0,"APSTAT") == "3")
		// {
			// ds_list.SetColumn(0,"APPGUBN", "R");
		// } else {
			// ds_list.SetColumn(0,"APPGUBN", "S");
		// }
		ds_list.SetColumn(0,"APPGUBN", fa_appgubn);
		ds_list.SetColumn(0,"SEQNO", 1);
		ds_list.SetColumn(0,"APDPT1", ds_draft.GetColumn(0,"DPT1"));
		ds_list.SetColumn(0,"APDPT1_N", ds_draft.GetColumn(0,"DPT1_N"));
		ds_list.SetColumn(0,"APNUM", ds_draft.GetColumn(0,"PSNO"));
		ds_list.SetColumn(0,"APRNK", ds_draft.GetColumn(0,"RANK_N"));
		ds_list.SetColumn(0,"APIRUM", ds_draft.GetColumn(0,"IRUM"));
	}
}

function fn_afterQuery3(errCode, errMsg) {
	//alert(ds_head_temp.rowcount);
	if(ds_head_temp.rowcount >  0)
	{
		tx_apdesc.value= ds_head_temp.getColumn(0, "CS649_TXT");
		ds_head.SetColumn(0,"FNAM",ds_head_temp.getColumn(0, "CS649_FNA"));
		disableFile();
	}
}


/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterOrg(errCode, errMsg) {
	if (ds_draft.RowCount() < 1)
	{
    	gfn_showAlert("CW00053","사번 이외의 ID 는");
		fn_close();
	}
	ed_docno.value = fa_docno;
	cb_dockind.value = fa_dockind;
	ds_org.Filter("DPT1 == "+ quote(""));
	//ds_tree.row = ds_tree.FindRow("DEPT",G_DPT1+"00");
	//결재자 부서정보
	ds_tree.row = ds_tree.FindRow("DEPT",ds_draft.GetColumn(0,"DEPT"));
	ufn_setDept(ds_tree.GetColumn(ds_tree.rowpos,"DEPT"));

    fn_query();
}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {

	//if ( ds_error.rowcount() > 0 ) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		completeFlag = true;
		// 2014.12.11 MG.J 반송버튼클릭시 반송완료여부 추가
		if( sendbackClick == 'Y') {
			sendbackFlag = true;
		}
		if(ds_mms.RowCount() > 0)
		{
			tit_clearActionInfo();

			//저장 처리 
			tit_addMultiActionInfo("cs04:CS0401001A_I3");

			tit_callService(
				"PDA_ACT"
				, ""
				, "ds_mms=ds_mms:A"
				, ""
				, ""
				, "fn_afterSmsSave"
				, true
				, true
				, true);
		}
	}

		/** 결재결과에 따른 부모창리턴값셋팅. **/
		// 상신완료 플래그를 확인하여 상신이 완료된 상태이면 "S", 상신 없이 저장이면 ""를 리턴.
		var complete = "";
		if( completeFlag ) {
			complete = "S";
		} else {
			complete = "";
		}

		// 반송완료 플래그를 확인하여 반송이 완료된 상태이면 "S", 반송 없이 저장이면 ""를 리턴.
		var sendback = "";
		if( sendbackFlag ) { 
			sendback = "S";
		} else {
			sendback = "";
		}

		completeArr = [complete, sendback];

		if(cb_dockind.Value == "7" or cb_dockind.Value == "9" or cb_dockind.Value == "4" ){ // 정기보수 실패 , 무상청구, 유상보수계약
		} else {
			parent.fn_query();
		}		

	//}

	fn_close();
}

function fn_afterSmsSave(errCode, errMsg) {
	//if ( ds_error.rowcount() > 0 ) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");

        Parent.fn_query();
		fn_close();

	}
}
