/********************************************************************************
* 기      능   : 결재선 추가 
********************************************************************************/
function btn_add_OnClick(obj,nX,nY)
{
// 오진수 사원 요청, 결재선 5명 이상 가능토록 한시적 적용 20151008
// 최인식 과장 요청, 결재선 7명 이상 가능토록 
if(G_USER_ID != "2024055") {

	if (ds_list.rowcount() == 7 )//|| ds_org.GetColumn(ds_org.currow,"DPT1") != G_DPT1) 타부서도 결재선 지정 가능
	{
		return;
	}
}
	if (ds_list.FindRow("APNUM",ds_org.GetColumn(ds_org.currow,"PSNO")) >= 0 )
	{
		return;
	}

	ds_list.AddRow();

	ds_list.SetColumn(ds_list.currow,"DOCNO", ed_docno.Text);
	ds_list.SetColumn(ds_list.currow,"DOCKIND", cb_dockind.Value);

	// 기안결재 완료 상태에서는 수신 상태
	// if(ds_head.rowcount > 0 && ds_head.GetColumn(0,"APSTAT") == "3")
	// {
		// ds_list.SetColumn(ds_list.currow,"APPGUBN", "R");
	// } else {
		// ds_list.SetColumn(ds_list.currow,"APPGUBN", "S");
	// }

	ds_list.SetColumn(ds_list.currow,"APPGUBN", fa_appgubn);
	ds_list.SetColumn(ds_list.currow,"SEQNO", ds_list.rowcount);
	ds_list.SetColumn(ds_list.currow,"APDPT1", ds_org.GetColumn(ds_org.currow,"DPT1"));
	ds_list.SetColumn(ds_list.currow,"APDPT1_N", ds_org.GetColumn(ds_org.currow,"DPT1_N"));
	ds_list.SetColumn(ds_list.currow,"APNUM", ds_org.GetColumn(ds_org.currow,"PSNO"));
	ds_list.SetColumn(ds_list.currow,"APRNK", ds_org.GetColumn(ds_org.currow,"RANK_N"));
	ds_list.SetColumn(ds_list.currow,"APIRUM", ds_org.GetColumn(ds_org.currow,"IRUM"));
	ds_list.SetColumn(ds_list.currow,"TELH", ds_org.GetColumn(ds_org.currow,"TELH"));

	ds_list.sort(grd_list.GetCellProp("Body",0,"colid"),false);
}

/********************************************************************************
* 기      능   : 결재선 제외
********************************************************************************/
function btn_remove_OnClick(obj)
{

	if (ds_list.GetColumn(ds_list.currow,"SEQNO") == 1 )
	{
		return;
	}

	ds_list.DeleteRow(ds_list.currow);	

	if (ds_list.RowCount() > 1)
	{
		ds_list.fireevent = false;
		for (i = 0; i < ds_list.rowcount; i++)
		{
			if (ds_list.GetColumn(i,"SEQNO") > 2) ds_list.SetColumn(i,"SEQNO", ds_list.rowcount - i);
		}
		ds_list.fireevent = true;
	}
	ds_list.sort(grd_list.GetCellProp("Body",0,"colid"),false);
}

function btn_openFile_OnClick(obj)
{
	fileDialog.open();

	if ( length(fileDialog.FilePath) == 0 
		|| length( fileDialog.FileName ) == 0 ) {
		return;
	}

	fileSize = f_tmp.GetLength(fileDialog.FilePath + "\\" + fileDialog.FileName );
	if ( fileSize <= 0 ) {
		gfn_showAlert("CW00048");
		return;
	}

	if ( fileSize > toNumber(F_SIZE)) {
		gfn_showAlert("CW00047", "4 Mbytes" );	
		return;
	}

	ds_head.setColumn(0, "FNAM", fileDialog.FileName);
	ds_head.setColumn(0, "FSIZE", fileSize);
	ds_head.setColumn(0, "FILE_PATH", fileDialog.FilePath);

	ed_fileName.value = fileDialog.FilePath + "\\" + fileDialog.FileName;
}

/********************************************************************************
* 기      능   : 첨부파일 삭제 
********************************************************************************/
function btn_deleteFile_OnClick(obj)
{
	ed_fileName.value = "";

	ds_list.setColumn(0, "FNAM", "");
	ds_list.setColumn(0, "FSIZE", 0);
	ds_list.setColumn(0, "FILE_PATH", "");
}


/********************************************************************************
* 기      능   : 첨부파일 저장  
********************************************************************************/
function btn_saveFile_OnClick(obj)
{
	saveDialog.FileName = ed_fileName.value;
    saveDialog.open();
    if ( length(saveDialog.FilePath) == 0 ) {
	   alert(saveDialog.FilePath);
        return;
    }

    if(length(ds_head.getColumn(0, "FNAM"))>0) {
		var arr = tit_fileRead(httpFile, "AP"
        , ds_head.getColumn(0, "FNAM")
        , saveDialog.FilePath, saveDialog.FileName);
    } else {
		var arr = tit_fileRead(httpFile, "AP"
        , ds_list.getColumn(0, "FNAM")
        , saveDialog.FilePath, saveDialog.FileName);
    }

    if ( arr[0] == 'E' ) {
        gfn_alert("Download failed : \n" + arr[1] );
    }
}

function enableFile()
{
	ed_fileName.Enable = true;
	btn_openFile.Enable = true;
	btn_deleteFile.Enable = true;
	btn_saveFile.Enable = false;
}

function disableFile()
{
	ed_fileName.Enable = false;
	btn_openFile.Enable = false;
	btn_deleteFile.Enable = false;

	// 파일 정보 보여주기 
	if ( length( ds_head.getColumn(0, "FNAM") ) > 0 ) {
		ed_fileName.value = tit_getLocalFileName(ds_head.getColumn(0, "FNAM")) ;
		btn_saveFile.enable = true;
	} else {
		btn_openFile.Enable = true;
	}
}

