/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
	gfn_clearGridSortMark(grd_head);

	ds_head.Clear();
	ds_list.Clear();

	ed_spt.Text = "";
	ed_pjt.Text = "";

	//ds_cond.SetColumn(0,"DPT1", G_DPT1);	
	ds_cond.SetColumn(0,"DPT1", vDPT);		// 수신부서지정(수신부서코드가 변경되면 조회 안되는 경우가 있어 공통함수로 수신부서정보 가져옴)

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs04:CS0401002A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_head=ds_head"
        , ""
        , "fn_afterQuery"
        , true);	
}




/********************************************************************************
* 기      능   : 결재선 조회
********************************************************************************/
function fn_query2() 
{
    ds_cond2.SetColumn(0,"DOCNO", ds_head.GetColumn(ds_head.currow,"DOCNO"));
	ds_cond2.SetColumn(0,"DOCKIND", ds_head.GetColumn(ds_head.currow,"DOCKIND"));
	ds_cond2.SetColumn(0,"APPGUBN", ds_head.GetColumn(ds_head.currow,"APPGUBN"));

	if(length(ds_cond2.GetColumn(0,"DOCNO")) < 1)
	{
		return;
	}

	var t_dockind = ds_head.GetColumn(ds_head.currow,"DOCKIND");
	var t_sql = "";

	switch(t_dockind) {
		case "1":	//MMPR
			t_sql = "cs04:CS0401002A_S4";
			break;
		case "2":	//FM청구
			t_sql = "cs04:CS0401002A_S5";
			break;
		case "3":	//정기보수견적
			t_sql = "cs04:CS0401002A_S6";
			break;
		case "4":	//정기보수계약
			t_sql = "cs04:CS0401002A_S7";
			break;
		case "5":	//정기보수매출변경
			t_sql = "cs04:CS0401002A_S8";
			break;
		case "6":	//정기보수중도해지
			t_sql = "cs04:CS0401002A_S9";
			break;
		case "7":	//정기보수실패보고
			t_sql = "cs04:CS0401002A_S10";
			break;
		case "8":	//정기보수업체이관
			t_sql = "cs04:CS0401002A_S11";
			break;
		case "9":	//무상보수
			t_sql = "cs04:CS0401002A_S12";
			break;
		case "10":	//무상보수중도해지
			t_sql = "cs04:CS0401002A_S13";
			break;
		case "12":	//수리공사견적
			t_sql = "cs04:CS0401002A_S14";
			break;
		case "13":	//수리공사계약
			t_sql = "cs04:CS0401002A_S15";
			break;
		case "14":	//수리공사재청구
			t_sql = "cs04:CS0401002A_S16";
			break;
		case "15":	//수리공사수주변경
			t_sql = "cs04:CS0401002A_S17";
			break;
		case "16":	//무상자재청구
			t_sql = "cs04:CS0401002A_S18";
			break;
		case "20":	//수리공사수주취소
			t_sql = "cs04:CS0401002A_S19";
			break;
		case "21":	//정기보수견적(주차)
			t_sql = "cs04:CS0401002A_S20";
			break;
		default:
			t_sql = "cs04:CS0401002A_S2";
			break;
	}

	// 서비스 실행 
	tit_clearActionInfo();
	tit_addSearchActionInfo(t_sql);
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery2"
        , true);
}


/********************************************************************************
* 기      능   : 의견저장
********************************************************************************/
function ufn_saveDesc(obj)
{
	// 저장할 데이터셋을 복사
	 gfn_copyDs(ds_list, ds_list_0, "FLAG="+quote("U"));

    // 자료가 없으면 리턴
	if (ds_list_0.rowcount < 1){
		gfn_showAlert("CW00010");
		return;
	}

	tit_clearActionInfo();
	tit_addMultiActionInfo("cs04:CS0401002A_U1");

	tit_callService(
		 ""
		, ""
		, "ds_list_0=ds_list_0:A"
		, ""
		, ""
		, "fn_afterSaveDesc"
        , "");	

}

/********************************************************************************
* 기      능   : 결재
********************************************************************************/
function fn_decide() {
	if(ds_head.RowCount() < 1)
	{
		return;
	}

	if(ds_list.RowCount() < 1)
	{
		return;
	}


	if(!gfn_showConfirm("CI00014", "문서번호 : "+ quote(ds_head.GetColumn(ds_head.currow,"DOCNO"))+ "결재")) return;

	ds_list.setColumn(ds_list.FindRow("APNUM","H"+G_USER_ID),"SEQSTAT","3");

	// 저장할 데이터셋을 복사
	 gfn_copyDs(ds_list, ds_list_0, "FLAG="+quote("U"));

	//tit_showDs(ds_list_0, false); return;
	tit_clearActionInfo();

 	//결재자 저장
 	tit_addMultiActionInfo("cs04:CS0401002A_U2");
 	tit_addMultiActionInfo("cs04:CS0401002A_U3");

	tit_callService(
		""
		, ""
		, "ds_list_0=ds_list_0:A"
		, ""
		, ""
		, "fn_afterDecide"
		, true);
}



/********************************************************************************
* 기      능   : 반송
********************************************************************************/
function fn_sendback() {
	if(ds_head.RowCount() < 1)
	{
		return;
	}

	if(ds_list.RowCount() < 1)
	{
		return;
	}


	if(!gfn_showConfirm("CI00014", "문서번호 : "+ quote(ds_head.GetColumn(ds_head.currow,"DOCNO")) + "반송")) return;

	ds_list.setColumn(ds_list.FindRow("APNUM","H"+G_USER_ID),"SEQSTAT","2");

	// 저장할 데이터셋을 복사
	 gfn_copyDs(ds_list, ds_list_0, "FLAG="+quote("U"));

	//tit_showDs(ds_list_0, false); return;
	tit_clearActionInfo();

 	//결재자 저장
 	tit_addMultiActionInfo("cs04:CS0401002A_U2");
 	tit_addMultiActionInfo("cs04:CS0401002A_U3");

	tit_callService(
		""
		, ""
		, "ds_list_0=ds_list_0:A"
		, ""
		, ""
		, "fn_afterDecide"
		, true);
}



/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() {
	if(ds_head.RowCount() < 1)
	{
		return;
	}

	if(ds_list.RowCount() < 1)
	{
		return;
	}

    if(ds_head.GetColumn(ds_head.currow,"APSTAT") != "2" && ds_head.GetColumn(ds_head.currow,"APSTAT") != "5"
       && ds_head.GetColumn(ds_head.currow,"APPGUBN") != "S")
    {
		return;
    }

	if(!gfn_showConfirm("CI00014", "문서번호 : "+ quote(ds_head.GetColumn(ds_head.currow,"DOCNO")) + "삭제")) return;

	ds_head.setColumn(ds_head.currow,"FLAG","D");

	// 저장할 데이터셋을 복사
	ds_head_0.ClearData();
	gfn_copyDs(ds_head, ds_head_0, "FLAG="+quote("D"));

	//tit_showDs(ds_list_0, false); return;
	tit_clearActionInfo();

 	//결재자 저장
 	tit_addMultiActionInfo("cs04:CS0401002A_D1");
 	tit_addMultiActionInfo("cs04:CS0401002A_D2");

	tit_callService(
		""
		, ""
		, "ds_head_0=ds_head_0:A"
		, ""
		, ""
		, "fn_afterDelete"
		, true);
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}
