********************************************************************************/
function ufn_setDescEnable()
{

	if( ds_list.GetColumn(ds_list.rowpos,"APNUM") != "H"+G_USER_ID)
	{
		tx_apdesc.ReadOnly = true;
	} else
	{
		tx_apdesc.ReadOnly = false;
	}
}

