/********************************************************************************
* 기      능   :  그리드 더블 클릭 
********************************************************************************/
function grd_head_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	var arg = "";

	if(ds_head.GetColumn(nRow,"DOCKIND") == "1")
	{
		arg += "fa_jqprnum=" + quote(ds_head.GetColumn(nRow,"DOCNO"));
	} 
	else if(ds_head.GetColumn(nRow,"DOCKIND") == "2")
	{
		arg += " fa_pjt=" + quote(ds_head.GetColumn(nRow,"PS_PSPID"));
		arg += " fa_reqnr=" + quote(ds_head.GetColumn(nRow,"REQNR"));
		arg += " fa_reqno=" + quote(ds_head.GetColumn(nRow,"DOCNO"));
		arg += " fa_lgort1=" + quote("");
		arg += " fa_vkgrp=" + quote("");
		arg += " fa_qtnum=" + quote("");
		arg += " fa_upn=" + quote(ds_head.GetColumn(nRow,"UPN2")); 
	} 
	else if(ds_head.GetColumn(nRow,"DOCKIND") == "16")
	{
		arg += " fa_pjt=" + quote(ds_head.GetColumn(nRow,"PS_PSPID"));
		arg += " fa_reqnr=" + quote(ds_head.GetColumn(nRow,"REQNR"));
		arg += " fa_reqno=" + quote(ds_head.GetColumn(nRow,"DOCNO"));
		arg += " fa_lgort1=" + quote("");
		arg += " fa_vkgrp=" + quote("");
		arg += " fa_qtnum=" + quote("");
	}
	else if(ds_head.GetColumn(nRow,"DOCKIND") == "3" || ds_head.GetColumn(nRow,"DOCKIND") == "21")
	{
		arg += " fa_reqno=" + quote(ds_head.GetColumn(nRow,"DOCNO")) + " fa_popgbn=A";
	} 
	else if(ds_head.GetColumn(nRow, "DOCKIND") == "12" || 				//수리공사견적
			ds_head.GetColumn(nRow, "DOCKIND") == "13" || 				//수리공사계약(수주)
			ds_head.GetColumn(nRow, "DOCKIND") == "14" || 				//수리공사재청구
			ds_head.GetColumn(nRow, "DOCKIND") == "15" || 				//수리공사수주변경
			ds_head.GetColumn(nRow, "DOCKIND") == "20")   				//수리공사수주취소
	{
		arg += " ar_qtnum=" + quote(ds_head.GetColumn(nRow, "QTNUM"));	//견적번호	
		arg += " ar_seq=" + quote(ds_head.GetColumn(nRow, "SEQ"));		//차수
		arg += " ar_upn=" + quote(ds_head.GetColumn(nRow, "UPN"));		//통합프로젝트번호
		arg += " ar_cst=" + quote(ds_head.GetColumn(nRow, "CST"));		//거래선
	}
	else if(ds_head.GetColumn(nRow,"DOCKIND") == "23") // 서비스 인수 
	{
		arg += " fa_docno=" + quote(ds_head.GetColumn(nRow,"DOCNO")) ;
		arg += " fa_pjt="   + quote(ds_head.GetColumn(nRow,"POSID")) ;
		arg += " gubun=QUERY";
	} 
	else {
		arg += " fa_docno=" + quote(ds_head.GetColumn(nRow,"DOCNO")) + " fa_popgbn=A";
	}
	//alert(ds_head.GetColumn(nRow,"TARGET"));
	gfn_openPopup(ds_head.GetColumn(nRow,"TARGET"), true, arg);	
}


/********************************************************************************
* 기      능   :  문서 선택시 결재선 조회
********************************************************************************/
function ds_head_OnRowPosChanged(obj,nOldRow,nRow)
{
	for(var i = 0; i< ds_list.RowCount(); i++)
	{
		if(ds_list.GetColumn(i,"FLAG") == "U")
		{
			if(!gfn_showConfirm("CI10001","이동"))
			{
				ds_head.fireevent = false;
			    ds_head.rowpos = nOldRow;
				ds_head.fireevent = true;
			    return;
			}
		}		
	}
	fn_query2();
}


/********************************************************************************
* 기      능   :  의견입력시 상태값 변경 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	gfn_changeFlag( obj, strColumnID, nRow, "FLAG" );
}



function cb_gubn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch (obj.index)
	{
		case 2: btn_delete.Enable = true; break;
		default: btn_delete.Enable = false; break;
	}
}

// 첨부파일 다운로드 
function grd_head_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 9 && length(ds_head.getColumn(nRow, "FNAM")) > 0 && ds_head.getColumn(nRow, "FSIZE") > 0) {
		// 첨부 파일 처리 
		saveDialog.FileName = tit_getLocalFileName(ds_head.getColumn(nRow, "FNAM"));
		if (saveDialog.open())
		{
			tit_fileRead(
				httpFile
				, "AP"
				, ds_head.getColumn(nRow, "FNAM")
				, saveDialog.FilePath
				, saveDialog.FileName);
		}
	}

	if(nCell == 8 && length(ds_head.GetColumn(nRow, "FNAM")) > 0) { // 첨부 이미지 VIEWER

		var arg = "fname_app="+quote(ds_head.GetColumn(nRow, "FNAM"))
		          + " fname="+quote(ds_head.GetColumn(nRow, "FNAM"));
		gfn_openPopup("CS0404001A", true, arg);

	}
}

function moveTab00_OnChanged(obj,nOldIndex,nNewindex)
{
	if( obj.TabIndex == 0) 
	{
		ds_list.Filter("APPGUBN == 'S'");
	}
	else
	{
		ds_list.Filter("APPGUBN == 'R'");
	}	
}

/********************************************************************************
* 기      능   : 서비스 본부 팀코드 가져오기
********************************************************************************/
function fn_setVkgrp(){

	//=========================수정 시작====================================
	//ds_vkgrp_cd.Filter("(CODE ='') OR (CODE >= 'EA1' AND CODE <= 'EFP')");
	// 2019 조직개편으로 지사추가 및 해외서비스 제외처리
	ds_vkgrp_cd.Filter("(CODE ='') OR (left(CODE,1) == 'E') AND (CODE != 'EH1')");
	//=========================수정 종료====================================

	var user_grp = gfn_getUserGroup();

	cb_vkgrp.Index = ds_vkgrp_cd.FindRow("CODE", G_SAP_USER_VGCD);
	// 전체 조회 가능 
	if(user_grp == "40"){
		// 2019 조직개편으로 지사추가 및 해외서비스 제외처리
		//ds_vkgrp_cd.Filter("(CODE ='') OR (CODE >= 'E97' AND CODE <= 'EFP')");
		cb_vkgrp.Index = 0;
		cb_vkgrp.Enable = true;
	} else {
		cb_vkgrp.Enable = true; // 2019.01.07 관리자 권한이 아닐지라도 다른 지사로 상신된 결재문서 보이도록 팀콤보 활성화 처리. Han J.H
	}

}


//=====================================파일첨부 관련 추가=====================================
//============================================================================================

//====================================파일첨부==============================
function btn_openFile_OnClick(obj)
{
	fileDialog.open();

	if ( length(fileDialog.FilePath) == 0 
		|| length( fileDialog.FileName ) == 0 ) {
		return;
	}

	fileSize = f_tmp.GetLength(fileDialog.FilePath + "\\" + fileDialog.FileName );
	if ( fileSize <= 0 ) {
		gfn_showAlert("CW00048");
		return;
	}

	if ( fileSize > toNumber(F_SIZE)) {
		gfn_showAlert("CW00047", "4 Mbytes" );	
		return;
	}

	ds_file.ClearData();
	ds_file.AddRow();		
	ds_file.setColumn(0, "FNAM", fileDialog.FileName);
	ds_file.setColumn(0, "FSIZE", fileSize);
	ds_file.setColumn(0, "FILE_PATH", fileDialog.FilePath);

	ed_fileName.value = fileDialog.FilePath + "\\" + fileDialog.FileName;
}

//=============================첨부파일 EDIT창 리셋========================
function btn_deleteFile_OnClick(obj)
{
	fileDialog.FileName = "";
	fileDialog.FilePath = "";
	fileSize   = "";
	ed_fileName.value = "";
	ds_file.ClearData();
	//ds_file.setColumn(0, "FNAM", "");
	//ds_file.setColumn(0, "FSIZE", 0);
	//ds_file.setColumn(0, "FILE_PATH", "");	
}

//=================================첨부파일 저장 및  DB 업데이트========================
function btn_saveFile_OnClick(obj)
{

	if(length( ed_fileName.value ) == 0 or ds_file.RowCount() < 1 )
	{
		gfn_showAlert("CW10025", "첨부할 문서가 없습니다.");
		return;
	}	

	if ( length(ds_head.GetColumn(ds_head.currow, "L_FNAM")) > 0 ) 
	{
	    // 전현정 대리 요청으로 기존 첨부 문서가 있을시 처리 불가토록 변경 : 2019.04.01
	    // 전현정 대리 요청으로 기간 , id 별로 재첨부 기능 추가 : 2019.04.18	    
	    if ( gfn_getCurrDate() > '20191231' && gfn_getUserGroup() == "40" )
	    {
			if(!gfn_showConfirm("CI00014", "선택하신 해당문서는 첨부문서가 존재함.<br> 기존 문서를 삭제 후 문서 " )) return;
	    }
	    else
	    {
			if ( G_USER_ID == "2017938" || G_USER_ID == "2022079" || G_USER_ID == "2027593" || G_USER_ID == "2029197" || G_USER_ID == "2020971" ||
			     G_USER_ID == "2021056" || G_USER_ID == "2027607" || G_USER_ID == "2016869" || G_USER_ID == "2018209" || G_USER_ID == "2019523" ||
		 	     G_USER_ID == "2025388" || G_USER_ID == "2026988" || G_USER_ID == "2030292" || G_USER_ID == "5012279" || G_USER_ID == "5012490" ||
		 	     G_USER_ID == "2021692" ) 
		    {
				if(!gfn_showConfirm("CI00014", "선택하신 해당문서는 첨부문서가 존재함.<br> 기존 문서를 삭제 후 문서 " )) return;
		    }
		    else
		    {
				gfn_showAlert("CW10025", "선택하신 해당문서는 첨부문서가 존재합니다.");   return;
		    }
	    }

	}
	else
	{
		if(!gfn_showConfirm("CI00014", "첨부파일" )) return;
	}


	if(length( ed_fileName.value ) > 0) 
	{
		if(length(ds_file.getColumn(0, "FILE_PATH")) > 1 ){
				// 첨부 파일 처리 
				var arr = tit_fileWrite(
										httpFile
										, "AP"
										, ds_file.getColumn(0, "FILE_PATH")
										, ds_file.getColumn(0, "FNAM")
										, ""
										, ""
										//, "" 
										, ds_head.GetColumn(ds_head.currow, "FNAM")
										);  

				// 처리 결과 확인하기 
				if( arr[0] == "S" ) {
					// 성공 
					ds_file.setColumn(0, "FNAM", arr[2]);	// 파일 명 
					ds_file.setColumn(0, "FSIZE", fileSize);					
					ds_file.setColumn(0, "DOCNO"  , ds_head.GetColumn(ds_head.currow, "DOCNO") );
					ds_file.setColumn(0, "DOCKIND", ds_head.GetColumn(ds_head.currow, "DOCKIND") );


					tit_clearActionInfo();					
					tit_addMultiActionInfo("cs04:CS0401002A_U9");
					tit_callService(
						""
						, ""
						, "ds_file=ds_file"
						, ""
						, ""
						, "fn_afterDecide"
						, ""
						, ""
						, true);

					btn_deleteFile_OnClick();

				} else {
					gfn_showAlert( arr[1] );
					return;
				}
			}
	}

}
