//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);
	ds_cond.setColumn(0, "MANDT", G_MANDT);

	//인계일 : 현재월의 첫날짜, 마지막날짜
	var firstDay = substr(gfn_getCurrDate(),0,6) + "01";
	var lastDay = ufn_getLeapLastDay(gfn_getCurrDate());
	cal_igd_fm.value = firstDay;
	cal_igd_to.value = lastDay;
	cal_isd.value = gfn_getCurrDate();

	var codeArr = ["CS114", "CS115", "CS113", "CS116", "CS112", "WBAP_STAT"];
	var dsObjArr = [ds_zspec1_cd, ds_ara_cd, ds_pst_cd, ds_agb_cd, ds_abg_cd, ds_apstat];
	var addTypeArr = [G_CODE_ALL, G_CODE_ALL, G_CODE_ALL, G_CODE_NONE, G_CODE_NONE, G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	ufn_enableBtn("save", false);
	ufn_enableBtn("draft", false);
	ufn_enableBtn("cancel", false);
	ufn_enableBtn("print", false);

	var user_grp = gfn_getUserGroup();

	gfn_initDs(ds_code_cond, true);

	if(user_grp == "30") { // 지방사무소일 경우
//		ds_code_cond.setColumn(0, "ARA", G_AREA_CODE);
		ds_code_cond.setColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_code_cond.setColumn(0, "ARA", '');
	}

	/*
	if(G_USER_ID == "2014599" || G_USER_ID == "2018209" || G_USER_ID == "2017750" || G_USER_ID == "2018900" ) {
		rd_dat.Enable = true;
	} else {
		rd_dat.Enable = false;
	}
	*/
	//=================위의 인수/인계 선택  공통권한으로 뺌==================
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS0602001A");
	ds_rcond.SetColumn(0, "GBN", "01");	
	vInout_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();

	if(vInout_auth == true) {
		rd_dat.Enable = true;
	} else {
		rd_dat.Enable = false;
	}
	//======================================================================


	tit_clearActionInfo();
	tit_addSearchActionInfo("cs06:CS0602001A_S7");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_code_cond"
        , "ds_bsu_cd=ds_list"
        , ""
        , "fn_afterCodeSearch"
        , true);
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	// 이동유형 설정 
	cb_zspec1.value = "";
//	cb_ara.value = G_LGORT;
//	cb_ara.value = "E5"; //임시 사용
	cb_pst.value = "";
	cb_agb.value = "";
	cb_abg.value = "";
	cb_fyn.value = "";
	cb_bsu.value = "";

	cb_ara.Index = 0;
	cb_jpgbn.index = 0;

	var user_grp = gfn_getUserGroup();

	if(user_grp == "30") { // 지방사무소일 경우
		ds_ara_cd.Filter("CODE == " + quote(G_AREA_CODE));
		cb_ara.Index = 1;
	}

	gfn_showMsg("CI00007");
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
