/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	//Project 번호 입력시 다른 조건들은 무시
	if ( gfn_isNull(ed_pjt, true) ) {
		if(gfn_getDayCnt(ds_days, cal_igd_fm.value, cal_igd_to.value) > 366) {
			gfn_showMsg("CW00009");
			return;
		}

		if ( gfn_isNull(cal_igd_fm, true) ) {
			gfn_showMsg("CW00001",st_tigd.value);
			return;
		}

		if ( gfn_isNull(cal_igd_to, true) ) {
			gfn_showMsg("CW00001",st_tigd.value);
			return;
		}

		if ( gfn_isNull(cb_jpgbn, true) ) {
			gfn_showMsg("CW00001",st_jpgbn.value);
			return;
		}
	}
/*
	if ( gfn_isNull(cb_ara, true ) ) {
		gfn_showMsg("CW00001",st_ara.value);
		return;
	}
*/
	if(ed_pjt.value == '' && cb_zspec1.value == '') {
		ds_cond.SetColumn(0, "GBN", "A");
	} else if(ed_pjt.value == '' && cb_zspec1.value <> '') {
		ds_cond.SetColumn(0, "GBN", "B");
	} else {
		ds_cond.SetColumn(0, "GBN", "");
	}

	t_zspec1 = cb_zspec1.value;
	var str_zspec1 = cb_zspec1.Text;

	if(str_zspec1 == "ALL") {
		str_zspec1 = "";
	}

	ds_cond.setColumn(0, "ARA", cb_ara.value);
	ds_cond.setColumn(0, "PJT", ed_pjt.value);
	ds_cond.setColumn(0, "PST", cb_pst.value);
//	ds_cond.setColumn(0, "ZSPEC1", cb_zspec1.value);
	ds_cond.setColumn(0, "ZSPEC1", str_zspec1);
	ds_cond.setColumn(0, "IGD_FM", cal_igd_fm.value);
	ds_cond.setColumn(0, "IGD_TO", cal_igd_to.value);
	ds_cond.setColumn(0, "DAT", rd_dat.value);

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	ds_list.ClearData();

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs06:CS0602001A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);




}
/********************************************************************************
* 기      능   : 삽입 20
********************************************************************************/
function fn_insert() {
	// 그리드에 row 추가 
	var newRow = gfn_addGridRow(grd_list, -1, "FLAG");
}
/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {
	if ( ds_list.rowCount() < 1 ) {
		// 삭제할 정보 없음. 

		return;
	}

	// Grid 삭제 처리 
	gfn_deleteGridRow( grd_list, ds_list.row, "FLAG" );
}
/********************************************************************************
* 기      능   : 저장 
* arr = draft 인 경우 기안 문서 생성 
********************************************************************************/
function fn_save(arr) {

	gfn_clearMsgBar();

	if ( !gfn_isChangeDs(ds_list, true) ) {
		return;
	}
