//	var bYear = substr(cal_isd.value,0,4);
//	var cYear = substr(gfn_getCurrDate(),0,4);

//	if(bYear < cYear) {
//		gfn_showAlert("이전년도로 신규인수접수가 불가능합니다.\n고객지원부에 문의바랍니다.(02-3670-0816)");
//		return;
//	}

	if(arr <> "draft") {

		// 필수 항목 체크 
		// 1. 시내구분 
		if ( gfn_isNull(cb_agb, true) ) {
			gfn_showMsg("CW00001",st_agb.value);
			return;
		}
		// 2. 빌딩구분 
