/*==================================================================================================
			호기정보에 'M'으로 시작하는 호기가 없으며, 문맥상 movingWolking을 'M'으로 표기한듯 보임
			--> 이것을 'W'로 변경함
			2019.10.15 7003043 김종남(방기태 차장 검토)

			if((substr(ds_list.GetColumn(i, "CS101_HNO"),0,1) == "L" || substr(ds_list.GetColumn(i, "CS101_HNO"),0,1) == "S" || 
				substr(ds_list.GetColumn(i, "CS101_HNO"),0,1) == "M") 
			&& (ds_list.GetColumn(i, "ZMUSAOCNT") <> ds_list.GetColumn(i, "CO_MG") || ds_list.GetColumn(i, "ZMUSAOCNT2") <> ds_list.GetColumn(i, "CO_MI"))) {
				gfn_showAlert("무상개월수가 일치하지 않아 인수가 불가능합니다.\n영업부 담당자에게 문의바랍니다.");
				return;
			}
                                                                                                    */
			if((substr(ds_list.GetColumn(i, "CS101_HNO"),0,1) == "L" || substr(ds_list.GetColumn(i, "CS101_HNO"),0,1) == "S" || 
				substr(ds_list.GetColumn(i, "CS101_HNO"),0,1) == "W") 
			&& (ds_list.GetColumn(i, "ZMUSAOCNT") <> ds_list.GetColumn(i, "CO_MG") || ds_list.GetColumn(i, "ZMUSAOCNT2") <> ds_list.GetColumn(i, "CO_MI"))) {
				gfn_showAlert("무상개월수가 일치하지 않아 인수가 불가능합니다.\n영업부 담당자에게 문의바랍니다.");
				return;
			}
//===================================================================================================
			if(substr(ds_list.GetColumn(i, "CS101_HNO"),0,1) != "J" ){
