			}

			// 물류 분사 PSD 트랜젝션 제어 20170831 End******//

			if(ds_list.GetColumn(i, "CS101_PST") == 'A1' && cal_isd.value >= ds_list.GetColumn(i, "CS101_IGD")) {
				ds_list2.AddRow();
				ds_list2.SetColumn(j, "FLAG", "U");
