				ds_list2.SetColumn(j, "MBO", parseInt(ds_list.GetColumn(i, "ZMUSAOCNT2")));
				ds_list2.SetColumn(j, "MYO", parseInt(ds_list.GetColumn(i, "ZMUSAOCNT2")));
//				ds_list2.SetColumn(j, "ARA", ds_list.GetColumn(i, "CS101_ARA"));
				ds_list2.SetColumn(j, "ARA", ds_bsu_cd.GetColumn(ds_bsu_cd.FindRow("CODE", cb_bsu.Value), "AREA"));
//				ds_list2.SetColumn(j, "BSU", cb_bsu.value);
				ds_list2.SetColumn(j, "BSU", ds_bsu_cd.GetColumn(ds_bsu_cd.FindRow("CODE", cb_bsu.Value), "CODE2"));
//				ds_list2.SetColumn(j, "GBU", ds_list.GetColumn(i, "BSU_GB"));
				ds_list2.SetColumn(j, "GBU", ed_bsu_gb.value);
				ds_list2.SetColumn(j, "ABG", cb_abg.value);
				ds_list2.SetColumn(j, "AGB", cb_agb.value);
				ds_list2.SetColumn(j, "FDT", cal_isd.value);
				ds_list2.SetColumn(j, "EDD", ''); //시스템 date(8자리)
				ds_list2.SetColumn(j, "EDT", ''); //시스템 time(6자리)
				ds_list2.SetColumn(j, "USR", G_USER_ID);
				ds_list2.SetColumn(j, "BLD", ds_list.GetColumn(i, "BLD"));
				ds_list2.SetColumn(j, "ATWRT", ds_list.GetColumn(i, "ATWRT"));
				ds_list2.SetColumn(j, "ATWTB", ds_list.GetColumn(i, "ATWTB"));

				// ds_list3 sms 전송 시작 *********************************************/
				ds_list2.SetColumn(j, "TRAN_PHONE", ed_bsu_hp.value);
				ds_list2.SetColumn(j, "TRAN_CALLBACK", '02-3670-0966');
				ds_list2.SetColumn(j, "TRAN_STATUS", '1');
