				ds_list2.SetColumn(j, "DOCNO"  , ds_list.GetColumn(i, "DOCNO"));

				//

				if(j == 0) {
					o_pjt = "";
				} else {
					o_pjt = ds_list2.GetColumn(j-1, "CS101_PJT");
				}
				n_pjt = ds_list2.GetColumn(j, "CS101_PJT");
//alert("o_pjt ==> [" + o_pjt + "]");
//alert("n_pjt ==> [" + n_pjt + "]");
				if(o_pjt <> n_pjt) {
					var rows = ds_list3.AddRow();
					ds_list3.CopyRow(rows, "ds_list2", ds_list2.row);
				}

				if(j == 0) {
					var rows = ds_list4.AddRow();
					ds_list4.CopyRow(rows, "ds_list2", 0);
				}

				j++;
			} else {
				if(ds_list.GetColumn(i, "CS101_PST") <> 'A1') {
					gfn_showMsg("CW00042", (parseInt(i) + 1));
					return;
				}

				if(cal_isd.value < ds_list.GetColumn(i, "CS101_IGD") || 
				   cal_isd.value < ds_list.GetColumn(i, "DATE2") || 
				   cal_isd.value < ds_list.GetColumn(i, "DATE1")) {
					var arr = [parseInt(i) + 1, st_isd.text];
					gfn_showMsg("CW00055", arr);
