		}
	}

	ds_list4.SetColumn(0, "MANDT", G_MANDT);
	ds_list4.SetColumn(0, "BSU", ds_bsu_cd.GetColumn(ds_bsu_cd.FindRow("CODE", cb_bsu.Value), "CODE2"));
	ds_list4.SetColumn(0, "RCNT", ds_list2.RowCount());

/*************************************************************/
/* arr = >> draft 기안 
/************************************************************/

	if(arr == "draft") {
		fn_makeDraft();
	} else {

	//	trace(ds_list3.SaveXML());
		tit_clearActionInfo();

		// 호기마스터 insert, update(zcst111)
		tit_addSelectExActionInfo("cs06:CS0602001A_S2", "cs06:CS0602001A_I1", "cs06:CS0602001A_U1");

