/********************************************************************************
* 기      능   : 취소 35
********************************************************************************/
function fn_cancel() {

	if ( !gfn_isChangeDs(ds_list, true) ) {
		return;
	}

	ds_list2.ClearData();

	var j = 0;

	// 인수 취소 조건
	// pst = A6
	// 승강기 : 인계년도와 작업년도 일치 . (주차 제외 , 박준성대리 요청)

	for(var i=0; i<=ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우
			if(ds_list.GetColumn(i, "CS101_PST") == 'A6' 
			   && length(ds_list.GetColumn(i, "MPS")) == 0 
			   && length(ds_list.GetColumn(i, "MYS")) == 0 
			   && ( (substr(ds_list.GetColumn(i, "CS101_IGD"),0,4) == substr(gfn_getCurrDate(),0,4) || G_USER_ID == "2017938")
			      ||(substr(ds_list.GetColumn(i, "CS101_HNO"),0,1) == "J") ) ) {
				ds_list2.AddRow();
				ds_list2.SetColumn(j, "FLAG", "U");
				ds_list2.SetColumn(j, "MANDT", G_MANDT);
				ds_list2.SetColumn(j, "CS101_PJT", ds_list.GetColumn(i, "CS101_PJT"));
				ds_list2.SetColumn(j, "CS101_HNO", ds_list.GetColumn(i, "CS101_HNO"));
				ds_list2.SetColumn(j, "CS101_PST", 'A6');
				ds_list2.SetColumn(j, "CS101_BSU", '');
				ds_list2.SetColumn(j, "CS101_ISD", '');
				ds_list2.SetColumn(j, "CS101_ISJ", '');
				ds_list2.SetColumn(j, "CS101_FYN", '');
				ds_list2.SetColumn(j, "CS101_FDT", '');
				ds_list2.SetColumn(j, "CS101_FSJ", '');
				ds_list2.SetColumn(j, "CS101_BPM", '');
				ds_list2.SetColumn(j, "CS101_GNO", '');
				ds_list2.SetColumn(j, "BSU", ds_list.GetColumn(i, "CS101_BSU"));
				ds_list2.SetColumn(j, "CS161_TQT", parseInt(ds_list.GetColumn(i, "CS161_TQT")) - 1);
				ds_list2.SetColumn(j, "ZZBSU", '');
				ds_list2.SetColumn(j, "ZBOSUIN", '');
				var POSID_1 = ds_list.GetColumn(i, "CS101_PJT") + "" + ds_list.GetColumn(i, "CS101_HNO");
				ds_list2.SetColumn(j, "POSID_1", POSID_1);

				// 물류 분사 PSD 트랜젝션 제어 20170831 Start*****//
				if(substr(ds_list.GetColumn(i, "CS101_HNO"),0,1) == "G") {						
					gfn_showAlert("CW00246");
					return;
				}
				// 물류 분사 PSD 트랜젝션 제어 20170831 End*****//

				// 호기별 인수 작업일 과 마감일자 체크 20160510  주차  제외 20160525

				if(substr(ds_list.GetColumn(i, "CS101_HNO"),0,1) != "J") {		
					var close_date = substr(gfn_getCurrDate(),0,6) + "01" ; 
					var pjthno     = ds_list.GetColumn(i, "CS101_PJT") + ds_list.GetColumn(i, "CS101_HNO") ;

					if( ds_list.GetColumn(i, "EDD") < close_date ) {
						 gfn_showAlert(pjthno + " 마감일 이전 작업 데이타는 인수 취소가 불가능 합니다.  서비스 관리팀에 문의 바랍니다.");
						 return;
					}
				}

				j++;
			} else {
				if(ds_list.GetColumn(i, "CS101_PST") <> 'A6') {
					gfn_showMsg("CW00042", (parseInt(i) + 1));
					return;
				}
				if(length(ds_list.GetColumn(i, "MPS")) <> 0) {
					gfn_showMsg("CW00022", (parseInt(i) + 1) + "번째의 무상공사시작일");
					return;
				}
				if(length(ds_list.GetColumn(i, "MYS")) <> 0) {
					gfn_showMsg("CW00022", (parseInt(i) + 1) + "번째의 무상일반시작일");
					return;
				}

				if(substr(ds_list.GetColumn(i, "CS101_HNO"),0,1) <> "J"){ // 주차의 경우 제외
					if(substr(ds_list.GetColumn(i, "CS101_IGD"),0,4) <> substr(gfn_getCurrDate(),0,4)) {
						gfn_showAlert((parseInt(i) + 1) + "번째의 호기는 " + substr(ds_list.GetColumn(i, "CS101_IGD"),0,4) + "년도 인계호기 입니다.\n취소가 불가능하므로 고객지원부에 문의 바랍니다.(02-3670-0816)");
						return;
					}
				}
			}

			gfn_initDs(ds_cond_chk, true);
			ds_cond_chk.SetColumn(0, "PJT", ds_list.GetColumn(i, "CS101_PJT"));
			ds_cond_chk.SetColumn(0, "HNO", ds_list.GetColumn(i, "CS101_HNO"));

			if(fn_cancelFChk() == false) { // 실패여부 체크
				return false;
			}

			if(fn_cancelMChk() == false) { // 무상발주여부 체크
				return false;
			}

			if(fn_cancelUChk() == false) { // 유상계약여부 체크
				return false;
			}

			if(fn_cancelHChk() == false) { // 회수등록여부 체크
				return false;
			}
		}
	}

	tit_clearActionInfo();

	// 취소 처리 
	// 인수인계정보(zcst101)
	tit_addSaveActionInfo(
        ""
        , "cs06:CS0602001A_U5"
        , ""
        , "FLAG");

	// 호기마스터(zcst111)
	tit_addSaveActionInfo(
        ""
        , "cs06:CS0602001A_D1"
        , ""
        , "FLAG");

	// 협력사별 보수대수 정보 update(zcst161) 
	tit_addSaveActionInfo(
        ""
        , "cs06:CS0602001A_U6"
        , ""
        , "FLAG");

	// 호기정보 update(zmaster02) 
	tit_addSaveActionInfo(
        ""
        , "cs06:CS0602001A_U7"
        , ""
        , "FLAG");

	// 자동발주 리스트 (zcst117)
	tit_addSaveActionInfo(
        ""
        , "cs06:CS0602002A_D1"
        , ""
        , "FLAG");

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list2:A"
        , ""
        , ""
        , "fn_afterCancel");
}

