			ufn_enableBtn("save", true);
			ufn_enableBtn("draft", true);
		} else if(cb_pst.value == 'A6') {
			ufn_enableBtn("save", false);
			ufn_enableBtn("draft", false);
		}
		if(cb_pst.value == 'A1') {
			ufn_enableBtn("cancel", false);
