//=========================================================================================
// [ PART 5 ]
// Detail Process Event 정의 ( User Define function )
// 사용자 정의 함수는 ufn_xxxx() 형태로 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 사용자 정의 함수  
********************************************************************************/
function ufn_getPro(hno) {
	var hgb, pro;

	hgb = substr(hno,0,1);

	switch(hgb) {
		case 'L' :
		case 'S' :
		case 'W' : pro = 'A';
		case 'J' : pro = 'B';
		case 'G' : pro = 'C';
		case 'F' : pro = 'D';
	}

	return pro;
}

function ufn_getMsg(curRow) {
	var msg;

	msg = "[" + ds_list.GetColumn(curRow, "CS101_PJT") + " " + substr(ds_list.GetColumn(curRow, "ZSITE_NM"),0,10) + "..]" + " 현장이 인수되었습니다";

	return msg;
}

function ufn_getGno(curRow) {
	var gno, pjt, pgb, hno, hgb;
	var g[10];

	pjt = ds_list.GetColumn(curRow, "CS101_PJT");
	pgb = substr(ds_list.GetColumn(curRow, "CS101_PJT"),0,1);
	hno = ds_list.GetColumn(curRow, "CS101_HNO");
	hgb = substr(ds_list.GetColumn(curRow, "CS101_HNO"),0,1);

	if(!(hgb == "L" || hgb == "S")) {
		return '';
	}

	switch(pgb) {
		case '0' :
		case '1' : g[0] = '1'; //자사
		case 'N' : g[0] = '2'; //교체
		case 'M' : g[0] = '3'; //타사
		case 'G' : g[0] = '4'; //PSD
		case 'J' : g[0] = '5'; //주차
		case 'F' : g[0] = '6'; //물류
		default  : g[0] = '9'; //기타
	}

	switch(pgb) {
		case 'J' :
		case 'M' :
		case 'N' :
		case 'G' :
		case 'F' : g[1] = '0';
		case 'E' : g[1] = '9';
		default  : g[1] = pgb;
	}

	return gno;
}

function ufn_getLeapLastDay(date) {
	var year,month,day;
	var lastDay = "31";

	if(date.length == 8 ) {
		year  = substr(date,0,4);
		month = substr(date,4,2);
		day   = substr(date,6,2);
	} else {
		return false;
	}

	if (year < '1900') return false;
	if (month < '01' || month > '12') return false;
	if (day < '01' || day > '31') return false;

	switch (month) {
		case '02' : if ((ParseInt(year)%4 == 0 && ParseInt(year)%100 != 0) || ParseInt(year)%400 == 0) {
			if (day <= 29) lastDay = 29;
		} else {
			if (day <= 28) lastDay = 28;
		}

		break;

		case '04' :
		case '06' :
		case '09' :
		case '11' : if (day <= 30) lastDay = 30;
	}

	return year+month+lastDay;
}

