function btn_report_OnClick(obj)
{
	var k = 1;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true){
			k ++;
		}
	}
	if(k > 2) {
		gfn_showAlert("한 건씩 출력이 가능합니다.");
		return;
	}

	// ubi 레포트 데이타 셋 
	ds_list_report.ClearData();
	ds_list_report.AddRow();

	if(ds_list.GetColumn(ds_list.currow, "CS101_PST") == "A6") {
		ds_report.SetColumn(0, "NAM", ds_list.GetColumn(ds_list.currow, "ZSITE_NM"));
		ds_report.SetColumn(0, "PJT", ds_list.GetColumn(ds_list.currow, "CS101_PJT"));
		ds_report.SetColumn(0, "CNT", ds_list.GetColumn(ds_list.currow, "CNT"));
		ds_report.SetColumn(0, "BSU", ds_list.GetColumn(ds_list.currow, "LIFNR_NM"));
		ds_report.SetColumn(0, "DAT", gfn_getCurrDate());
		ds_report.SetColumn(0, "ADR", ds_list.GetColumn(ds_list.currow, "ADR"));

		ds_list_report.CopyRow(0, ds_list, ds_list.currow);

		Dialog("CS06::CS0602001B.xml", "");
	} else {
		gfn_showAlert("인수된 호기만 승인서 출력이 가능합니다.");
		return;
	}
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == 'CHK') {
		if(ds_list.GetColumn(nRow, "CS101_PST") == 'A1' && ds_list.GetColumn(nRow, "KUNNR") == '') {
			if(ds_list.GetColumn(nRow, "CHK") == '1') {
				obj.FireEvent = false;
				ds_list.SetColumn(nRow, "CHK", "0");
				if(ed_cnt.value == "") {
					gfn_showAlert("고객번호가 존재하지 않습니다. SAP에서 보수 고객등록을 먼저 하시기 바랍니다.");
				}
				obj.FireEvent = true;
			}
		}
	}
}

function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	ed_cnt.value = "";

	// 성적서 유형 cellHead : 검사상태 DSCODE _LHR 20121129 수정
	if(nCell == 17) {
		var t_hno = ds_list.GetColumn(nRow, "CS101_PJT") + ds_list.GetColumn(nRow, "CS101_HNO");
		var gbn = substr(ds_list.GetColumn(nRow, "CS101_HNO"), 0, 1);
		var pjt = ds_list.GetColumn(nRow, "CS101_PJT");
		var hno = t_hno;

		fn_goNextForm(gbn,pjt,hno);
	}
}

function fn_goNextForm(gbn,pjt,hno) {
	var formID = "";

	switch (gbn) {

		case 'L':
			formID = "PS0101001L";
			break;			

		case 'S':
			formID = "PS0101001S";
			break;

	}

	var arg = "PRE_PJT="+quote(pjt);
		arg += " PRE_HNO="+quote(hno);

	gfn_openPopup(formID, true, arg);
}

function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 3) {
		var arg = "posid="+quote(ds_list.GetColumn(nRow,"CS101_PJT"));
		gfn_openWin("WB0199061A",arg);
	}
// 결재문서 조회 하기 
	if(nCell == 47) {

		if(ds_list.GetColumn(nRow,"DOCNO") <> "") {

			var arg = "gubun=QUERY"
					+ " fa_pjt="+quote(ds_list.GetColumn(nRow,"CS101_PJT"))
					+ " fa_docno="+quote(ds_list.GetColumn(nRow,"DOCNO"));
			gfn_openPopup("CS0602001C",true,arg);		
		}

	}
}

