/********************************************************************************
* 기      능   : 기안 
********************************************************************************/
function fn_draft() {

	fn_save("draft");
}
function fn_makeDraft()	{

	var p_pjt, p_hno;
	var writeDocno = true;
	var checkPlan = true;
	var o_pjt, n_pjt;

	for(var i=0; i<ds_list2.RowCount(); i++) { 

		if(i == 0) {
			o_pjt = ds_list2.GetColumn(i, "PJT");
		}
		n_pjt = ds_list2.GetColumn(i, "PJT");

		if(o_pjt <> n_pjt) {
			gfn_showAlert("동일 프로젝트만 기안 작성이 가능합니다.");
			return;
		}

		p_pjt = ds_list2.GetColumn(i, "PJT");
		p_hno = ds_list2.GetColumn(i, "HNO");

		// 기안 진행 중 체크 
		if(!fn_checkDocno(p_pjt, p_hno)) {		
			writeDocno = false;
		} 
		if(ds_list2.GetColumn(i, "ZZBSU_B") == "") {
			checkPlan = false;
		}

	}

	if(writeDocno && checkPlan) {
//alert("A");	
		var arg = "gubun=DRAFT"
		        + " fa_pjt=" + quote(p_pjt);
		gfn_openPopup("CS0602001C",true,arg);
	} else {
		if(!checkPlan) {
			gfn_showAlert("인수계획 정보가 없습니다. 준공예정현장 인수계획 등록에서 인수업체를 지정해주세요.");
			return;
		} else {
			gfn_showAlert("기안문서가 존재합니다.");
			return;
		}
	}
}

/********************************************************************************
* 기      능   : 기안 진행 중 체크 
********************************************************************************/
function fn_checkDocno(i_pjt, i_hno){

	ds_checkDocno.ClearData();
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs06:CS0602001A_S14");
	tit_callService(
        ""
        , ""
        , ""
        , "ds_checkDocno=ds_list"
        , "PJT=" + quote(i_pjt) + " HNO="+quote(i_hno)						
        , ""						
		, true							
		, false							
		, true
	);
	// 반송의 경우 재기안 가능 
	if(ds_checkDocno.GetColumn(0, "APSTAT") == "2" || ds_checkDocno.GetColumn(0, "APSTAT") == "5" 
	    || ds_checkDocno.GetColumn(0, "APSTAT") == "") {
		return true;
	} else {
		return false;
	}
}
