#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var pjt;
var hno;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm();
	// 공통코드 
	var codeArr = ["CS204"];
	var dsObjArr = [ds_rsn];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	fn_query();
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {
	// 검사 항목 리스트 조회
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs06:CS0605001B_S1");

	tit_callService(
        ""
        , ""
        , ""
        , "ds_list_p=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	parent.fn_query();
	close();
}
/********************************************************************************
* 기      능   : 확인  
********************************************************************************/
function fn_confirm() {
	close();
}
//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");

	fn_queryHno();
}
function fn_queryHno() {
	ds_list_h.ClearData();
	ds_condD.ClearData();

	ds_condD.AddRow();
	ds_condD.SetColumn(0, "PJT" , pjt);
	ds_condD.SetColumn(0, "HNO" , hno);

	// 현장 검사항목 및 원인부터 조회
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs06:CS0605001D_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_condD"
        , "ds_list_h=ds_list"
        , ""
        , "fn_afterQueryHno"
        , true);
}
function fn_afterQueryHno() {

	cb_ntc_qc.Value = ds_list_h.GetColumn(0, "CS107_NTC_QC");
}

function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	ds_list_h.SetColumn(ds_list_h.GetRowPos(),"QIC_M" , ds_list_p.GetColumn(nRow,"CS106_QMC")) ;
	ds_list_h.SetColumn(ds_list_h.GetRowPos(),"QMR_M" , ds_list_p.GetColumn(nRow,"CS106_QMR")) ;
}
function fn_save() {

	var status1 = 0;
	var status2 = 0;
	ds_save.ClearData();
	ds_save_ntc.ClearData();

	if(length(cb_ntc_qc_m.value) > 0 and cb_ntc_qc_m.value != cb_ntc_qc.value) {
		ds_save_ntc.AddRow();
		ds_save_ntc.CopyRow(0, "ds_condD", 0);	
		ds_save_ntc.SetColumn(0, "CS107_NTC_QC", cb_ntc_qc_m.Value);

		status1 = 1 ;
	} 

	for(var i=0; i<ds_list_h.rowCount(); i++) {
		if(ds_list_h.GetColumn(i, "FLAG") == "U") {
			var rows = ds_save.AddRow();
			ds_save.CopyRow(rows, "ds_list_h", i);
			ds_save.SetColumn(rows, "FLAG", ds_list_h.GetColumn(i, "FLAG"));	
		}
	}

	if(ds_save.RowCount() > 0 ) status2 = 1 ;


	if( status1 + status2 > 0) {

		tit_clearActionInfo();
		if ( status1 > 0 ) tit_addMultiActionInfo("cs06:CS0605001D_U1"); // ZCST107 
		if ( status2 > 0 ) tit_addMultiActionInfo("cs06:CS0605001D_U2"); // ZCST108 

		tit_callService(
			""
			, ""
			, "ds_list2=ds_save ds_list=ds_save_ntc"
			, ""
			, ""
			, "fn_afterSave"						
			, true			);	

	} else {
		gfn_showAlert("CW00005");  //  변경된 정보가 없습니다.  
	}

}
function fn_afterSave(errCode, errMsg) 
{
	gfn_showAlert("CI00003");
	fn_query();
}

function ds_list_h_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	gfn_changeFlag( obj, strColumnID, nRow, "FLAG" );
}

function grd_list_h_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);

	cal_temp.top = rect[1] + 120;
	cal_temp.Left = rect[0] - 80; 
	cal_temp.Visible = true;
	if(nCell == 11) {
		cal_temp.value = ds_list_h.getColumn(nRow, "CS108_JYD"); // 조치 예정일 
	}

	cal_temp.SetFocus();
}

/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	if(grd_list_h.GetClickedCell() == 11) {
		ds_list_h.SetColumn(ds_list_h.row, "CS108_JYD", strText);
	}

	obj.Visible = false;
}

/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우 
********************************************************************************/
function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
}
