/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	var user_grp = gfn_getUserGroup();
/*
	if(div_code.cb_mid.value == "" && div_code.cb_small.value == "") {
		gfn_showMsg("CW00001", st_bsu.Text);
		return;
	}
*/
	if(cb_gbn.value == "") {
		gfn_showMsg("CW00001", trim(st_gbn.Text));
		return;
	}

	if(length(trim(cb_year.value)) == 0 && length(trim(cb_month.value)) > 0) {
		gfn_showMsg("CW00001", trim(st_yymm.Text));
		return;
	}

	//년도필드 필수체크	
	//if(gfn_isNull(cb_year, true, st_yymm.value))
	//	return;
	//월필드 필수체크	
	//if(gfn_isNull(cb_month, true, st_yymm.value))
	//	return;

	var fYear  = "";
	var fMonth = "";

	if(length(trim(cb_year.value)) > 0) {
		fYear = parseInt(ds_cond.GetColumn(0,"YY"));
	}

	if(length(trim(cb_month.value)) > 0) {
		fMonth = parseInt(ds_cond.GetColumn(0,"MM"));
	}

	if(length(trim(fYear)) == 0) {
		ds_cond.SetColumn(0,"SEL",'A');
	}

	if(length(trim(fYear)) > 0 && length(trim(fMonth)) == 0) {
		ds_cond.SetColumn(0,"SEL",'B');
	}

	if(length(trim(fYear)) > 0 && length(trim(fMonth)) > 0) {
		ds_cond.SetColumn(0,"SEL",'C');
	}

	ds_cond.SetColumn(0,"YYMM",ds_cond.GetColumn(0,"YY")+ds_cond.GetColumn(0,"MM"));

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		//====================================수정시작=======================================
		/*
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
		*/
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));

		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));

		} else if(div_code.cb_big.value == '3') { // 협력사 콤보1이 팀 
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));

		} else { // 협력사 콤보1이 부서일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_small.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "DPT", substr(trim(div_code.cb_mid.value),0,2));
			ds_cond.SetColumn(0, "BSU", '');
		}		
		//====================================수정끝=======================================	
	}

	if(length(trim(ed_pjt.value)) > 0) {
		ds_cond.SetColumn(0, "PJT", ed_pjt.value);
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs06:CS0607001A_S1");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list=ds_list"
		, ""
		, "fn_afterQuery"
		, true);
}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {

	if(G_USER_ID <> "2020971" && G_USER_ID <> "2017938" && G_USER_ID <> "2021056" && G_USER_ID <> "2021153") {

		if(substr(trim(ed_pjt.value),0,1) == "M") { //M프로젝트는 회수입력 불가
			gfn_showAlert("CW00201");
			return;
		}
	}

	if(trim(ed_pjt.value) == trim(ed_pjt2.value) && trim(cb_hno.value) == trim(cb_hno2.value)){
		gfn_showAlert("교체 전/후가 동일합니다.");
		return;
	}
	if(length(trim(ed_pjt.value)) == 0) {
		gfn_showAlert("CW00001", st_pjt.Text);
		return;
	}
	if(length(trim(ed_pjt2.value)) == 0) {
		gfn_showAlert("CW00001", st_pjt2.Text);
		return;
	}
	if(length(trim(ed_pjt.value)) > 0 && length(trim(cb_hno.value)) == 0) {
		gfn_showAlert("CW00001", st_hno.Text);
		return;
	}
	if(length(trim(ed_pjt2.value)) > 0 && length(trim(cb_hno2.value)) == 0) {
		gfn_showAlert("CW00001", st_hno2.Text);
		return;
	}
	if(length(trim(cb_gbn.value)) == 0) {
		gfn_showAlert("CW00001", st_gbn.Text);
		return;
	}

	if(!gfn_showConfirm("CI00014", "[" + cb_gbn.text + "]")) return;

	gfn_initDs(ds_cond_chk, true);
	gfn_initDs(ds_cond_chk2, true);
	gfn_initDs(ds_cond_chk3, true);
	gfn_initDs(ds_cond_chk4, true);
	gfn_initDs(ds_cond_chk5, true);
	gfn_initDs(ds_cond_chk6, true);
	gfn_initDs(ds_cond_chk7, true);
	gfn_initDs(ds_cond_chk8, true);

	ds_cond_chk.SetColumn(0,"PJT",ed_pjt.value);
	ds_cond_chk.SetColumn(0,"HNO",cb_hno.value);
	ds_cond_chk2.SetColumn(0,"PJT",ed_pjt.value);
	ds_cond_chk2.SetColumn(0,"HNO",cb_hno.value);
	ds_cond_chk3.SetColumn(0,"PJT",ed_pjt2.value);
	ds_cond_chk3.SetColumn(0,"HNO",cb_hno2.value);
	ds_cond_chk4.SetColumn(0,"PJT",ed_pjt2.value);
	ds_cond_chk4.SetColumn(0,"HNO",cb_hno2.value);
	ds_cond_chk5.SetColumn(0,"PJT",ed_pjt.value);
	ds_cond_chk5.SetColumn(0,"HNO",cb_hno.value);
	ds_cond_chk6.SetColumn(0,"PJT",ed_pjt2.value);
	ds_cond_chk6.SetColumn(0,"HNO",cb_hno2.value);
	ds_cond_chk7.SetColumn(0,"PJT",ed_pjt2.value);
	ds_cond_chk7.SetColumn(0,"HNO",cb_hno2.value);
	ds_cond_chk8.SetColumn(0,"PJT",ed_pjt.value);
	ds_cond_chk8.SetColumn(0,"HNO",cb_hno.value);

	if(G_USER_ID <> "2020971" && G_USER_ID <> "2017938" && G_USER_ID <> "2021056" && G_USER_ID <> "2021153") {
		if(fn_saveChk1() == false) { // 인수여부 체크
			return false;
		}
	}

//	if(cb_gbn.value == "A" && fn_saveChk2() == false) { // 유상계약여부 체크(회수일때만 체크)
	if(G_USER_ID <> "2020971" && G_USER_ID <> "2017938" && G_USER_ID <> "2021056" && G_USER_ID <> "2021153") {
		if(fn_saveChk2() == false) {
			return false;
		}
	}


// **** 이전 프로젝트 실패여부 체크  **** //
	if(fn_saveChk3A() == false) { // 실패여부 체크 (호기마스터 체크)
		return false;
	}

	if(fn_saveChk3A2() == false) { // 실패여부 체크 ( 실패 기안 진행 체크)
		return false;
	}
// **** 이전 프로젝트 실패여부 체크  **** //

// **** 신규 프로젝트 실패여부 체크  **** //
	if(fn_saveChk3B() == false) { // 실패여부 체크 (호기마스터 체크)
		return false;
	}

	if(fn_saveChk3B2() == false) { // 실패여부 체크 ( 실패 기안 진행 체크)
		return false;
	}
// **** 신규 프로젝트 실패여부 체크  **** //

	if(fn_saveChk4() == false) { // 유상계약여부 체크
		return false;
	}

	// if(G_USER_ID <> "2017750" && G_USER_ID <> "2017938" && G_USER_ID <> "2023466") {
		// if(fn_saveChk5() == false) { // 무상발주여부 체크
			// return false;
		// }
	// }

	if(fn_saveChk6() == false) { // 무상발주여부 체크
		return false;
	}

	if(fn_saveChk7() == false) { // 기존 프로젝트, 호기 중복여부 체크
		return false;
	}

	if(fn_saveChk8() == false) { // 신규 프로젝트, 호기 중복여부 체크
		return false;
	}

	gfn_initDs(ds_save_list, true);
	ds_save_list.SetColumn(0,"FLAG","U");
	ds_save_list.SetColumn(0,"PJN",ed_pjt.value);
	ds_save_list.SetColumn(0,"HNN",cb_hno.value);
	ds_save_list.SetColumn(0,"PJO",ed_pjt2.value);
	ds_save_list.SetColumn(0,"HNO",cb_hno2.value);
	ds_save_list.SetColumn(0,"GBN",cb_gbn.value);

	var sql = "";
	if(length(trim(ed_pjt2.value)) > 0) {
		sql = "cs06:CS0607001A_I1";
	} else {
		sql = "cs06:CS0607001A_I2";
	}

	tit_clearActionInfo();

	// 회수교체 프로젝트 등록(ZCST172)
	tit_addSaveActionInfo(
		""
		, sql
		, ""
		, "FLAG");

	// 호기마스터 상태값 UPDATE(zcst111)
	tit_addSaveActionInfo(
		""
		, "cs06:CS0607001A_U1"
		, ""
		, "FLAG");

	tit_callService(
        ""
        , ""
        , "ds_save_list=ds_save_list"
        , ""
        , ""
        , "fn_afterSave");
}

function fn_saveChk1() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs06:CS0607001A_S4");
	tit_callService(
		""
		, ""
		, "ds_cond_chk=ds_cond_chk"
		, "ds_cnt=ds_cnt"
		, ""
		, ""
		, true
		, true
		, true
		);

	if(ds_cnt.GetColumn(0,"CNT") == 0) {
		gfn_showAlert("CW00196");
		return false;
	}

	return true;
}

