function fn_saveChk3A() {
	ds_cnt2.ClearData();
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs06:CS0607001A_S6");
	tit_callService(
		""
		, ""
		, "ds_cond_chk3=ds_cond_chk2"
		, "ds_cnt2=ds_cnt3"
		, ""
		, ""
		, true
		, true
		, true
		);

	if(ds_cnt2.GetColumn(0,"CNT") > 0) {
		//현재 실패 또는 삭제 상태의 데이터가 있습니다. 다시 한번 확인 바랍니다.
		gfn_showAlert("CW00200");
		return false;
	}

	return true;
}

