// component Event 정의 : 각 Component 별 이벤트 
//=========================================================================================
/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	gfn_sortGrid(obj,nCell);
}

function ufn_openPjt() {
	var arg = "pjt="+quote(ed_pjt.Text)+" gbn='A'";
	gfn_openPopup("CS9601002A",true,arg);
}

function ufn_openPjt2() {
	var arg = "pjt="+quote(ed_pjt2.Text)+" gbn='B'";
	gfn_openPopup("CS9601002A",true,arg);
}

function fn_searchHno() {
	if(length(ed_pjt.value) > 0) {
		gfn_initDs(ds_cond_hno, true);
		ds_cond_hno.setColumn(0, "PJT", ed_pjt.value);

		tit_clearActionInfo();
		tit_addSearchActionInfo("cs06:CS0607001A_S2");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond_hno"
			, "ds_hno=ds_list"
			, ""
			, ""
			, true);
	}
}

function fn_searchHno2() {
	if(length(ed_pjt2.value) > 0) {
		gfn_initDs(ds_cond_hno, true);
		ds_cond_hno.setColumn(0, "PJT", ed_pjt2.value);

		tit_clearActionInfo();
		tit_addSearchActionInfo("cs06:CS0607001A_S3");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond_hno"
			, "ds_hno2=ds_list"
			, ""
			, ""
			, true);
	}
}

function btn_init_OnClick(obj)
{
	ed_pjt.value = "";
	ed_pjt2.value = "";

	gfn_initDs(ds_hno, true);
	gfn_initDs(ds_hno2, true);
}
