// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var user_grp = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

    user_grp = gfn_getUserGroup();

	// 현재 일자 기준 2개월 후로 설정
	var firstDay = gfn_getAfterMonth(substr(gfn_getCurrDate(),0,6), 2) + "01";
	var lastDay = gfn_getLeapLastDay(firstDay);
	cal_fm.value = firstDay;
	cal_to.value = lastDay;

	// 설치 대지역
	var codeArr = ["CS115"];
	var dsObjArr = [ds_zzara_cd];
	var addTypeArr = [G_CODE_ALL];

	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	//=========== 조건 분리=======================
	if (G_USER_GROUP == 'Z30' || G_USER_GROUP == 'Z40')
	{		}
	else
	{
		btn_save.Enable = false;
	}

	if(gfn_getAuthGroup("ZCS07")) 
	{
		ds_cond.SetColumn(0,"CHK" , "A" );
		if(G_SAP_USER_VGCD == "E97")       //주차
		{
			ds_cond.SetColumn(0,"CHK" , "B" );
		} 

		if(G_VEND_CODE  == "0000009922") //정보기술팀
		{
			ds_cond.SetColumn(0,"CHK" , "D" );
		} 		

	}
	else 
	{
		if(G_SAP_USER_VGCD == "E97") 
		{
			ds_cond.SetColumn(0,"CHK" , "B" );
		} 
		else 
		{
			ds_cond.SetColumn(0,"CHK" , "C" );
		}
	}
	//=============================================

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs06:CS0610002A_S2");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_bsu_cd=ds_list"
        , ""
        , ""
        , true						
		, false							
		, true); 

	fn_setInit();
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_gbn2.Index = 0;
}

/********************************************************************************
* 기      능   : 
********************************************************************************/
function fn_setInit() {

	//======  해당 프로그램은 40권한으로 부여===========
	if (user_grp >= "20")
	{
		user_grp = "40";   
	}
    //==================================================


	// 협력사 권한 제한
	if(user_grp == "10") { 
		cb_gbn.Index = 1 ;			// 입력여부 - 입력
		cb_gbn.Enable   = false ;	// 입력여부 - 수정불가
		cb_zzara.Enable = false ;	// 지역여부 - 수정불가
		cb_bsu.Enable   = false ;	// 업체여부 - 수정불가
		ds_cond.SetColumn(0,"BSU" , G_LGORT);
	} else {
		cb_zzara.Index = 0 ;
		cb_gbn.Index = 0 ;
	}
	// 서비스 마케팅팀 외에 저장 버튼 권한 없음 
	if(gfn_getAuthGroup("ZCS07")) {
		btn_upload.Visible = true;
		cb_isd.Visible = true;
		st_isd.Visible = true;
		cb_gbn.Visible = true;
		st_gbn.Visible = true;
	}
	else{
		//cb_bsu.Enable = false;    // 모든 사람이 다 선택 가능하게 수정(20201030  이준석 과장 요청)
		cb_vkgrp.Enable = false;
	}	
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		//====================================수정시작=======================================
		/*
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
		*/
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));

		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));

		} else if(div_code.cb_big.value == '3') { // 협력사 콤보1이 팀 
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));

		} else { // 협력사 콤보1이 부서일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_small.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "DPT", substr(trim(div_code.cb_mid.value),0,2));
			ds_cond.SetColumn(0, "BSU", '');
		}				
		//====================================수정끝=======================================
	}

//trace(ds_cond.SaveXML());

	ds_list.ClearData();

	gfn_clearGridSortMark(grd_list);

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs06:CS0610002A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002", ds_list.RowCount()+"");

}

