function fn_close() {
	close();
}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {

	gfn_clearMsgBar();

	if(user_grp == "10") { 
		gfn_showAlert("변경 권한이 없습니다.");
		return;
	}

	ds_list2.ClearData();

	if(length(cb_bsu.Value) == 0) {
		gfn_showAlert("업체를 지정하십시오.");
		return;
	}

	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우 
			if(length(ds_list.GetColumn(i, "CS101_ISD")) == 0) {
				var rows = ds_list2.AddRow();
				ds_list2.CopyRow(rows,"ds_list",i);	
				ds_list2.SetColumn(rows, "ZZBSU_B" , cb_bsu.Value);
			}
		}
	}

//	trace(ds_list2.SaveXML());

	if(ds_list2.RowCount() == 0) {
		gfn_showAlert("저장할 데이타가 없습니다.");		
		return;
	}

	tit_clearActionInfo();

	tit_addMultiActionInfo("cs06:CS0610002A_U1");	// ZMASTER02

	tit_addSelectExActionInfo("cs06:CS0610002A_S3", "cs06:CS0610002A_I1", ""); // ZCST102 저장이력 테이블 

	tit_callService(
		""
		, ""
		, "ds_list=ds_list2"
		, ""
		, ""
		, "fn_afterSave"
		, true 
		);
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		fn_query();
	}
}

/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if(nCell == 0) {
		// 전체 선택
		gfn_selectAllGrid2(obj,0,0,true);
	} else {
		// 그리드 정렬 처리
		gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {
	gfn_saveToExcel(grd_list, true);
}

function btn_pjt_OnClick(obj) {
	var arg = "pjt="+quote(ed_pjt.Text);
	gfn_openPopup("CS9601002A",true,arg);
}

function cb_bsu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	cb_vkgrp.value = ds_bsu_cd.GetColumn(ds_bsu_cd.FindRow("BSU",strCode),"VKGRP");
}

