* 기      능   : 일괄업로드
********************************************************************************/
function btn_upload_OnClick(obj)
{
	gfn_openPopup("CS0610002C", true);

}

function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var vHeadText = grd_list.GetCellProp("HEAD",nCell,"text");
	//if(nCell == 23) {
	if (vHeadText == "계획변경이력") {
		var arg = "pjt="  + quote(ds_list.GetColumn(nRow,"POSID"))
				+ " hno=" + quote(substr(ds_list.GetColumn(nRow,"POSID_1"),6,3)) ;

		gfn_openPopup("CS0610002B", true, arg);
	}
}
