var cRow = null;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

	var firstDay = substr(gfn_getCurrDate(),0,6) + "01";
	cal_balju_fm.value = firstDay;
	cal_balju_to.value = gfn_getCurrDate();
	cb_gubun.value = "A";
	cb_daesang.value = "A";
//	cal_balju_fm.Enable = false;
//	cal_balju_to.Enable = false;
	ed_pjt.SetFocus();

	var codeArr = ["CS117"];
	var dsObjArr = [ds_pst_cd];
	var addTypeArr = [G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	var user_grp = gfn_getUserGroup();
/*
	if(user_grp <> "10") {
		ufn_enableBtn("query", false);
	}
*/
	ufn_enableBtn("trans", false);
	ufn_enableBtn("delete", false);
	ufn_enableBtn("save", false);
	cb_pst.Enable = false;
	btn_balju.Enable = false;

	Tab00.tab1.SetFocus();
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_pst.index = 0;
	Tab00.tab1.SetFocus();
	gfn_showMsg("CI00007");
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

