/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	if(gfn_isNull(cb_gubun, true)) {
		var arr = [st_gubun.value];
		gfn_showMsg("CW00001", arr);
		return;
	}

	if(gfn_isNull(cb_daesang, true)) {
		var arr = [st_daesang.value];
		gfn_showMsg("CW00001", arr);
		return;
	}

	if(gfn_isNull(cb_jpgbn, true)) {
		var arr = [st_jpgbn.value];
		gfn_showMsg("CW00001", arr);
		return;
	}

	var user_grp = gfn_getUserGroup();

	if(user_grp == "40") { //고객지원부일때 조회조건중 pjt나 협력사를 선택해야 한다.
		if(ed_pjt.value == "" && div_code.cb_mid.value == "" && div_code.cb_small.value == "") {
			gfn_showMsg("CW00001","프로젝트번호 또는 협력사");
			return;
		}
	}

	ds_cond.SetColumn(0, "MANDT", G_MANDT);
	ds_cond.SetColumn(0, "GBN", '');
	ds_cond.SetColumn(0, "PJT", trim(ed_pjt.value));
	ds_cond.SetColumn(0, "POSID", trim(ed_pjt.value));
	ds_cond.SetColumn(0, "GUBUN", trim(cb_gubun.value));
	ds_cond.SetColumn(0, "DAESANG", trim(cb_daesang.value));
	ds_cond.SetColumn(0, "BALJU_FM", cal_balju_fm.value);
	ds_cond.SetColumn(0, "BALJU_TO", cal_balju_to.value);
	ds_cond.SetColumn(0, "CB1", trim(div_code.cb_big.value));
	ds_cond.SetColumn(0, "CB2", trim(div_code.cb_mid.value));
	ds_cond.SetColumn(0, "CB3", trim(div_code.cb_small.value));
	ds_cond.SetColumn(0, "PST", cb_pst.value);

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
	}

	// sort mark 삭제 
	gfn_clearGridSortMark(Tab00.tab1.grd_list);
	ds_list.row = -1;
	ds_list.ClearData();

	var sql;

	if(cb_gubun.value == "A") { // 미발주
		sql = "cs11:CS1102001A_S1";
	} else { // 발주
		sql = "cs11:CS1102001A_S2";
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo(sql);

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

function fn_query2(cRow) {
	ds_cond2.SetColumn(0, "MANDT", G_MANDT);
	if(cb_daesang.value == "A") {
		ds_cond2.SetColumn(0, "GBN", 'A');
	} else {
		ds_cond2.SetColumn(0, "GBN", 'B');
	}
	ds_cond2.SetColumn(0, "PJT", trim(ds_list.GetColumn(cRow,"POSID")));
	ds_cond2.SetColumn(0, "BSU", trim(ds_list.GetColumn(cRow,"ZZBSU")));
	if(cb_gubun.value == "A") {
		ds_cond2.SetColumn(0, "PST", "");
	} else {
		ds_cond2.SetColumn(0, "PST", trim(ds_list.GetColumn(cRow,"PST")));
	}
	ds_cond2.SetColumn(0, "JPGBN", cb_jpgbn.Value);

	// sort mark 삭제 
	gfn_clearGridSortMark(Tab00.tab2.grd_list2);
	ds_list2.row = -1;
	ds_list2.ClearData();

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs11:CS1102001A_S3");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_list2=ds_list"
        , ""
        , "fn_afterQuery2"
        , true);
}

