function fn_save2() {
	if ( ds_list2.rowCount() < 1 ) {
		// 삭제할 정보 없음. 
		return;
	}

	ds_list3.ClearData();
	ds_list4.ClearData();

	gfn_clearMsgBar();

	if ( !gfn_isChangeDs(ds_list2, true) ) {
		return;
	}

	if ( gfn_isNull(Tab00.tab2.cal_baljudate_in, true) ) {
		var arr = [Tab00.tab2.st_baljudate_in.value];
		gfn_showMsg("CW00001", arr);
		return;
	}
/*
	if ( gfn_isNull(Tab00.tab2.ed_upche_in, true) ) {
		var arr = [Tab00.tab2.st_upche_in.value];
		gfn_showMsg("CW00001", arr);
		return;
	}
*/
	if(cb_daesang.value == "B") { // 무상일반
		if ( gfn_isNull(Tab00.tab2.cal_ipjudate_in, true) ) {
			var arr = [Tab00.tab2.st_ipjudate_in.value];
			gfn_showMsg("CW00001", arr);
			return;
		}

		if(Tab00.tab2.cal_ipjudate_in.value < Tab00.tab2.cal_baljudate_in.value) {
			var arr = [Tab00.tab2.st_ipjudate_in.value, Tab00.tab2.st_baljudate_in.value];
			gfn_showMsg("CW00036", arr);
			return;
		}
	}

	var tDate = replace(Tab00.tab2.cal_baljudate_in.value,".","");

	for(var i=0; i<ds_list2.RowCount(); i++) {
		if(ds_list2.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우		
			if(parseInt(trim(ds_list2.GetColumn(i, "MON"))) < 1) { // 무상개월수가 1보다 작은 경우
				var arr = [parseInt(i) + 1];
				gfn_showMsg("CW00181", arr);
				ds_list2.row = i;
				return;
			}

			if(trim(ds_list2.GetColumn(i, "PST")) == "A2") {
				gfn_showMsg("CW00175", arr);
				ds_list2.row = i;
				return;
			}

			if(ds_list2.GetColumn(i, "FDT") > tDate) {
				var arr = [parseInt(i) + 1, Tab00.tab2.st_baljudate_in.value, st_insudate.value];
				gfn_showMsg("CW00037", arr);
				ds_list2.row = i;
				return;
			}

			if(trim(ds_list2.GetColumn(i, "BSU")) == "") {
				var arr = [parseInt(i) + 1, st_bsu_chk.value];
				gfn_showMsg("CW00170", arr);
				ds_list2.row = i;
				return;
			}

			if(trim(ds_list2.GetColumn(i, "BPM")) == "") {
				var arr = [parseInt(i) + 1, st_bpm_chk.value];
				gfn_showMsg("CW00170", arr);
				ds_list2.row = i;
				return;
			}

			if(trim(ds_list2.GetColumn(i, "ARA")) == "") {
				var arr = [parseInt(i) + 1, st_ara_chk.value];
				gfn_showMsg("CW00170", arr);
				ds_list2.row = i;
				return;
			}

			if(trim(ds_list2.GetColumn(i, "JUJ")) == "") {
				var arr = [parseInt(i) + 1, st_juj_chk.value];
				gfn_showMsg("CW00170", arr);
				ds_list2.row = i;
				return;
			}

			if(trim(ds_list2.GetColumn(i, "BUJ")) == "") {
				var arr = [parseInt(i) + 1, st_buj_chk.value];
				gfn_showMsg("CW00170", arr);
				ds_list2.row = i;
				return;
			}

			if(trim(ds_list2.GetColumn(i, "GBU")) <> "1") {
				var arr = [parseInt(i) + 1, st_buj_chk.value];
				gfn_showMsg("[" + (parseInt(i) + 1 + "") + "행] 의 호기는 직영현장이 아닙니다.");
				ds_list2.row = i;
				return;
			}

			var nday = substr(tDate,6,2);
			var nDate, cDate;
			var PJTHNO;

			PJTHNO = ds_list2.GetColumn(i, "PJT") + ds_list2.GetColumn(i, "HNO");

			if(nday <> "01") {
				nDate = gfn_getAfterMonth(substr(tDate,0,6),ds_list2.GetColumn(i, "MON"));
				cDate = nDate + "" + nDay;
				cDate = gfn_getBeforeDate(cDate, 1);
			} else {
				nDate = gfn_getAfterMonth(substr(tDate,0,6),parseInt(ds_list2.GetColumn(i, "MON")) - 1);
				cDate = nDate + "" + gfn_getLastDayByStr(substr(nDate,0,6));
			}

			ds_list2.SetColumn(i, "FLAG", "U");		
			ds_list2.SetColumn(i, "BHD", cDate);
			ds_list2.SetColumn(i, "PJTHNO", PJTHNO);

			if(ds_list2.GetColumn(i, "SEQ") == "") {
				ds_list2.SetColumn(i, "CBS", Tab00.tab2.cal_baljudate_in.value);
			} else {
				ds_list2.SetColumn(i, "CBS", "");
			}

			ds_list2.SetColumn(i, "JUC", Tab00.tab2.ed_upche_in.value);
			ds_list2.SetColumn(i, "GND", cb_daesang.value);
			if(cb_daesang.value == "A") {
				ds_list2.SetColumn(i, "HYN", "");
			} else {
				ds_list2.SetColumn(i, "HYN", "Y");
			}
			ds_list2.SetColumn(i, "BGT", Tab00.tab2.cal_baljudate_in.value);

			if((ds_list2.GetColumn(i, "ST2") <= Tab00.tab2.cal_baljudate_in.value 
			   && ds_list2.GetColumn(i, "SM2") >= Tab00.tab2.cal_baljudate_in.value)
			   || (ds_list2.GetColumn(i, "ST2") <= ds_list2.GetColumn(i, "BHD") 
			   && ds_list2.GetColumn(i, "SM2") >= ds_list2.GetColumn(i, "BHD"))) {
				var arr = [parseInt(i) + 1];
				gfn_showMsg("CW00039", arr);
				ds_list2.row = i;
				return;
			}

			var posid_1 = "";

			gfn_initDs(ds_cond_chk, true);
			gfn_initDs(ds_cond_chk2, true);
			gfn_initDs(ds_cond_chk4, true);
			ds_cond_chk.SetColumn(0, "PJT", ds_list2.GetColumn(i, "PJT"));
			ds_cond_chk.SetColumn(0, "HNO", ds_list2.GetColumn(i, "HNO"));
			ds_cond_chk2.SetColumn(0, "PJT", ds_list2.GetColumn(i, "PJT"));
			ds_cond_chk2.SetColumn(0, "HNO", ds_list2.GetColumn(i, "HNO"));
			ds_cond_chk2.SetColumn(0, "BGT", ds_list2.GetColumn(i, "BGT"));
			ds_cond_chk2.SetColumn(0, "BMT", ds_list2.GetColumn(i, "BMT"));

			posid_1 = ds_list2.GetColumn(i,"PJT") + ds_list2.GetColumn(i,"HNO");

			ds_cond_chk4.SetColumn(0, "POSID_1", posid_1);
			ds_cond_chk4.SetColumn(0, "GND", ds_list2.GetColumn(i,"GND"));
			ds_cond_chk4.SetColumn(0, "MON", ds_list2.GetColumn(i,"MON"));

			if(fn_save2Chk() == false) { // 발주개월수 체크(화면에 나타난 발주개월수와 실제 ZMASTER02에 입력된 발주개월수 비교)
				return false;
			}

			if(fn_cancelFChk() == false) { // 실패여부 체크
				return false;
			}

			if(fn_UChk() == false) { //유상계약과 중복여부 체크
				return false;
			}

			var rows3 = ds_list3.AddRow();
			ds_list3.CopyRow(rows3, "ds_list2", i);

			var rows4 = ds_list4.AddRow();
			ds_list4.CopyRow(rows4, "ds_list2", i);
		} else {
			ds_list2.SetColumn(i, "FLAG", "");
			ds_list2.SetColumn(i, "BHD", "");
		}
	}

	tit_callService(
        "CS1102001A_ACT"
        , "save"
        , "ds_list3=ds_list3 ds_list4=ds_list4"
        , "ds_error=ds_error"
        , ""
        , "fn_afterApproval");
}

function fn_cancelFChk() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs06:CS0602001A_S12");
	tit_callService(
		""
		, ""
		, "ds_cond_chk=ds_cond_chk"
		, "ds_cnt=ds_cnt"
		, ""
		, ""
		, true
		, true
		, true
		);

	if(ds_cnt.GetColumn(0,"CNT") > 0) {
		gfn_showAlert("CW00223");
		return false;
	}

	return true;
}

