/********************************************************************************
* 기      능   : 전송 60
********************************************************************************/
function fn_trans() {
	if ( ds_list.rowCount() < 1 ) {
		// 전송할 정보 없음. 
		return;
	}

	gfn_clearMsgBar();

	if ( !gfn_isChangeDs(ds_list, true) ) {
		return;
	}

	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우
			if(ds_list.GetColumn(i, "PST") <> 'A1') { //진행상태가 작성이 아닐경우
				ds_list.SetColumn(i, "FLAG", "");
				var arr = [parseInt(i) + 1];
				gfn_showMsg("CW00042", arr);
				ds_list.row = i;				
				return;
			} else {
				ds_list.SetColumn(i, "FLAG", "U");
			}
		} else {
			ds_list.SetColumn(i, "FLAG", "");
		}
	}

	tit_clearActionInfo();

    // 무상보수 발주정보 update(zcst116) 
	tit_addSaveActionInfo(
        ""
        , "cs11:CS1102001A_U1"
        , ""
        , "FLAG");

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list"
        , ""
        , ""
        , "fn_afterTrans");
}
